) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `tbl_sanpham`
--

INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`) VALUES
(16, 'tour miền bắc', '232323', '5000000', 2, '1.jpg', '<h3>Li&ecirc;n tuyến T&acirc;y Bắc h&agrave;nh tr&igrave;nh trải nghiệm độc đ&aacute;o</h3>\r\n\r\n<p>T&acirc;y Bắc đ&atilde; trở th&agrave;nh một dấu ấn kh&ocirc;ng phai mờ trong suy tưởng như: Ho&agrave; B&igrave;nh, Điện Bi&ecirc;n, Sơn La, Phong Thổ v&agrave; Lai Ch&acirc;u. Giấc mơ được tới thăm v&ugrave;ng đất tr&aacute;ng lệ v&agrave; n&ecirc;n thơ nay nhiều người muốn đặt ch&acirc;n đến một lần. Cả v&ugrave;ng n&uacute;i đất đai với những đồng b&agrave;o d&acirc;n tộc thiểu số, l&agrave;ng mạc, v&agrave; cả những th&aacute;c nước. Những thửa ruộng bậc thang giữa thi&ecirc;n nhi&ecirc;n như đ&aacute;nh thức t&ocirc;i trở về l&agrave; một đứa trẻ thơ say sưa ngủ qu&ecirc;n trong khu rừng. Những d&atilde;y n&uacute;i non h&ugrave;ng vĩ tr&ugrave;ng điệp, những bản sương giăng, những đ&egrave;o m&acirc;y phủ, những ruộng bậc thang kỳ vỹ. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!</p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>H&agrave;nh tr&igrave;nh li&ecirc;n tuyến sẽ đưa qu&yacute; kh&aacute;ch trải nghiệm qua c&aacute;c nẻo đường:</p>\r\n\r\n<p>- Chi&ecirc;m ngưỡng th&aacute;c&nbsp;<strong>Dải Yếm</strong>, rừng th&ocirc;ng&nbsp;<strong>bản &Aacute;ng</strong>, đồi ch&egrave; h&igrave;nh tr&aacute;i tim, l&agrave;ng hoa cải, hoa mận tại cao nguy&ecirc;n&nbsp;<strong>Mộc Ch&acirc;u</strong>.</p>\r\n\r\n<p>-&nbsp;<strong>Th&aacute;c Bạc</strong>&nbsp;&ndash; một trong những th&aacute;c nước đẹp nhất&nbsp;<strong>Việt Nam</strong>.</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch tham quan, t&igrave;m hiểu về phong tục, tập qu&aacute;n của đồng b&agrave;o người&nbsp;Th&aacute;i.</p>\r\n\r\n<p>- Di t&iacute;ch gắn liền với chiến thắng&nbsp;<strong>Điện Bi&ecirc;n Phủ</strong>.</p>\r\n', '<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>NG&Agrave;Y 01: H&Agrave; NỘI &ndash; MỘC CH&Acirc;U &ndash; SƠN LA ( ĂN TRƯA,TỐI)</h3>\r\n\r\n<p>S&aacute;ng: Q&uacute;y kh&aacute;ch tập trung tại điểm hẹn (<strong>67-69 Trần Nh&acirc;n T&ocirc;ng, L&ecirc; Đại H&agrave;nh, Hai B&agrave; Trưng, H&agrave; Nội</strong>&nbsp;- Rạp xiếc trung ương), Xe v&agrave; HDV đ&oacute;n qu&yacute; kh&aacute;ch khởi h&agrave;nh đi<strong>&nbsp;Mộc Ch&acirc;u</strong>, tr&ecirc;n đường đi qu&yacute; kh&aacute;ch sẽ dừng ch&acirc;n ăn s&aacute;ng tại nh&agrave; h&agrave;ng (chi ph&iacute; tự t&uacute;c).</p>\r\n\r\n<p>Tr&ecirc;n đường đi&nbsp;đo&agrave;n dừng ch&acirc;n v&agrave; nghỉ ngơi v&agrave; chụp ảnh tại&nbsp;đ&egrave;o&nbsp;<strong>Thung Khe&nbsp;&ndash; H&ograve;a B&igrave;nh</strong>&nbsp;hay c&ograve;n được gọi l&agrave; đ&egrave;o<strong>&nbsp;Đ&aacute; Trắng</strong>, được d&acirc;n t&igrave;nh ca ngợi l&agrave; &ldquo;Ch&acirc;u &Acirc;u của H&ograve;a B&igrave;nh&rdquo;.&nbsp; Điểm đặc biệt l&agrave; thời tiết ở đ&acirc;y kh&aacute; th&uacute; vị. Tr&ecirc;n đ&egrave;o&nbsp;<strong>Đ&aacute; Trắng</strong>, một ng&agrave;y bạn c&oacute; thể được trải nghiệm đủ 4 m&ugrave;a trong năm. Xe tiếp tục đưa đo&agrave;n di chuyển l&ecirc;n thị trấn&nbsp;<strong>Mộc Ch&acirc;u</strong>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/12/24/14/ivivu-deo-thung-khe.gif\" /></p>\r\n\r\n<p><em>Đ&egrave;o Thung Khe.</em></p>\r\n\r\n<p>Trưa: Đo&agrave;n&nbsp;ăn trưa tại nh&agrave; h&agrave;ng với những m&oacute;n ăn mang đậm hương vị v&ugrave;ng cao, sau bữa ăn đo&agrave;n di chuyển về kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>Chiều: Q&uacute;y kh&aacute;ch tham quan N&ocirc;ng trường thăm đồi ch&egrave; (&nbsp;<strong>đồi ch&egrave; tr&aacute;i tim</strong>). T&igrave;m hiểu về cuộc sống lao động tại n&ocirc;ng trường ch&egrave; Mộc Ch&acirc;u, lưu lại những tấm h&igrave;nh chụp xanh ng&aacute;t giữa thảo nguy&ecirc;n đồi ch&egrave; m&ecirc;nh m&ocirc;ng.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/31/11/ivivu-tour-mien-bac-4n3d-ha-noi-hoa-binh-son-la-dien-bien-moc-chau-doi-che-trai-tim.jpg\" /></p>\r\n\r\n<p><em>Đồi Ch&egrave; Tr&aacute;i Tim.</em></p>\r\n\r\n<p>Sau đ&oacute;, qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>Rừng Th&ocirc;ng Bản &Aacute;ng</strong>, qu&yacute; kh&aacute;ch tham quan Vườn d&acirc;u Chimi, nơi đ&acirc;y qu&yacute; kh&aacute;ch sẽ được tham quan, trải nghiệm, tận mắt chứng kiến quy tr&igrave;nh chăm s&oacute;c v&agrave; thu hoạch d&acirc;u. Q&uacute;y kh&aacute;ch thưởng thức v&agrave; tự tay h&aacute;i những quả d&acirc;u ch&iacute;n (Chi ph&iacute; h&aacute;i d&acirc;u tự t&uacute;c).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/09/17/14/ivivu-rung-thong-ban-ang.gif\" /></p>\r\n\r\n<p>&nbsp;<em>Rừng Th&ocirc;ng Bản &Aacute;ng.</em></p>\r\n\r\n<p>Q&uacute;y kh&aacute;ch tham quan&nbsp;<strong>Th&aacute;c Dải Yếm</strong>&nbsp;(hay c&ograve;n gọi l&agrave; th&aacute;c N&agrave;ng, th&aacute;c Bản Vặt), nhằm v&iacute; vẻ đẹp của th&aacute;c nước như xu&acirc;n sắc của người con g&aacute;i tuổi trăng tr&ograve;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/10/06/16/ivivu-tour-mien-bac-6n5d-sai-gon-moc-chau-son-la-sapa-thac-dai-yem.jpg\" /></p>\r\n\r\n<p><em>Th&aacute;c Dải Yếm.</em></p>\r\n\r\n<p>Tham quan&nbsp;<strong>Cầu K&iacute;nh T&igrave;nh Y&ecirc;u</strong>&nbsp;&ndash; cầu k&iacute;nh 5D được x&acirc;y dựng tr&ecirc;n cao, với hai đầu gắn tr&ecirc;n triền n&uacute;i, phần cầu k&iacute;nh bắc qua s&ocirc;ng với tầm nh&igrave;n tho&aacute;ng đ&atilde;ng kh&ocirc;ng hạn chế, gi&uacute;p du kh&aacute;ch c&oacute; thể tha hồ ngắm cảnh xứ Mộc si&ecirc;u đẹp từ tr&ecirc;n cao v&agrave; ngay cả dưới ch&acirc;n m&igrave;nh. (v&eacute; tham quan cầu k&iacute;nh qu&yacute; kh&aacute;ch tự t&uacute;c).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2021/05/05/15/ivivu-cau-kinh-tinh-yeu.jpg\" /></p>\r\n\r\n<p><em>Cầu K&iacute;nh T&igrave;nh Y&ecirc;u.</em></p>\r\n\r\n<p>Tối: Đo&agrave;n ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại&nbsp;<strong>Mộc Ch&acirc;u</strong>.</p>\r\n\r\n<h3>NG&Agrave;Y 02: SƠN LA &ndash; ĐIỆN BI&Ecirc;N (ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng: Q&uacute;y kh&aacute;ch d&ugrave;ng bữa s&aacute;ng v&agrave; khởi h&agrave;nh đi&nbsp;<strong>Điện Bi&ecirc;n</strong>, tr&ecirc;n đường qu&yacute; kh&aacute;ch c&oacute; dịp chi&ecirc;m ngưỡng ngắm cảnh đẹp n&uacute;i rừng&nbsp;T&acirc;y Bắc&nbsp;với c&aacute;c l&agrave;ng Th&aacute;i ven bờ s&ocirc;ng Đ&agrave;.</p>\r\n\r\n<p>Tr&ecirc;n đường qu&yacute; kh&aacute;ch sẽ Chinh phục&nbsp;đ&egrave;o&nbsp;<strong>Pha Đin</strong>&nbsp;- một trong &ldquo;Tứ Đại Đỉnh Đ&egrave;o&rdquo; những đường đ&egrave;o d&agrave;i v&agrave; hiểm trở nhất Việt Nam, ranh giới 2 tỉnh Sơn La v&agrave; Điện Bi&ecirc;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/10/12/16/ivivu-tour-mien-bac-6n5d-sai-gon-moc-chau-son-la-sapa-deo-pha-din.jpg\" /></p>\r\n\r\n<p><em>Đ&egrave;o Pha Đin.</em></p>\r\n\r\n<p>Đến&nbsp;<strong>Điện Bi&ecirc;n</strong>&nbsp;qu&yacute; kh&aacute;ch tham quan Sở chỉ huy chiến dịch&nbsp;<strong>Điện Bi&ecirc;n Phủ,&nbsp;hầm Đại Tướng V&otilde; Nguy&ecirc;n&nbsp;</strong>Gi&aacute;p, Tham mưu trưởng&nbsp;<strong>Ho&agrave;ng Văn Th&aacute;i</strong>&nbsp;v&agrave; Bộ chỉ huy chiến dịch năm xưa.</p>\r\n\r\n<p>Trưa: Q&uacute;y kh&aacute;ch d&ugrave;ng cơm trưa v&agrave; nghỉ ngơi.</p>\r\n\r\n<p>Chiều: Q&uacute;y kh&aacute;ch Đo&agrave;n đến tham quan c&aacute;c di t&iacute;ch gắn liền với chiến thắng&nbsp;<strong>Điện Bi&ecirc;n Phủ</strong>&nbsp;lịch sử: Bảo t&agrave;ng lịch sử&nbsp;<strong>Điện Bi&ecirc;n Phủ</strong>, Nghĩa trang liệt sỹ A1, Đồi A1&nbsp;-&nbsp;Nơi đ&acirc;y c&oacute; 644 ng&ocirc;i mộ l&agrave; những chiến sỹ qu&acirc;n d&acirc;n ta đ&atilde; hy sinh anh dũng trong chiến dịch Điện Bi&ecirc;n Phủ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/07/10/16/ivivu-bao-tang-dien-bien.gif\" /></p>\r\n\r\n<p><em>Bảo T&agrave;ng Điện Bi&ecirc;n Phủ.</em></p>\r\n\r\n<p><strong>Hầm Đờ C&aacute;t</strong>, Cầu Mường Thanh, s&ocirc;ng Nậm Rốn, Tượng đ&agrave;i chiến thắng&nbsp;<strong>Điện Bi&ecirc;n Phủ</strong>&nbsp;-&nbsp;Những địa danh vẫn c&ograve;n mang đầy dư &acirc;m oai h&ugrave;ng của cuộc chiến mang &yacute; nghĩa lịch sử của to&agrave;n d&acirc;n tộc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/07/10/16/ivivu-ham-docat-db.gif\" /></p>\r\n\r\n<p><em>Hầm Đờ C&aacute;t.</em></p>\r\n\r\n<p>Tối: Ăn tối tại nh&agrave; h&agrave;ng hoặc Qu&yacute; kh&aacute;ch c&oacute; thể đặt ăn tối v&agrave;giao lưu văn nghệ tại&nbsp;<strong>Bản Mển</strong>&nbsp;- bản của&nbsp;người Th&aacute;i đen&nbsp;(chi ph&iacute; xem văn nghệ tự t&uacute;c). Nghỉ đ&ecirc;m tại&nbsp;<strong>Điện Bi&ecirc;n</strong>.</p>\r\n', 1, 4),
(17, 'tour hcm-phú quốc', '1222', '5200000', 2, '2.jpg', '<h3>Kh&aacute;m Ph&aacute; Vẻ Đẹp Hoang D&atilde; C&aacute;c Đảo Ph&uacute; Quốc</h3>\r\n\r\n<p>Nếu bạn muốn&nbsp;kh&aacute;m ph&aacute; một thi&ecirc;n đường tr&ecirc;n biển, sống động, đầy kỳ th&uacute; h&atilde;y đến với Ph&uacute; Quốc! Bạn sẽ được thỏa th&iacute;ch chi&ecirc;m ngưỡng vẻ đẹp thật k&igrave; diệu khi từ tr&ecirc;n cao nh&igrave;n xuống v&agrave; bao qu&aacute;t hết cả v&ugrave;ng trời biển ph&iacute;a Nam Ph&uacute; Quốc từ tr&ecirc;n ca bin c&aacute;p treo. C&aacute;p treo từ Cảng An Thới ra H&ograve;n Thơm Ph&uacute; Quốc với qu&atilde;ng đường d&agrave;i 7.899,9m, được Tổ chức Guinness trao tặng Chứng nhận C&aacute;p treo d&agrave;i nhất thế giới.&nbsp;Với lợi thế gồm c&aacute;c d&ograve;ng Cano mới v&agrave; hiện đại nhất Ph&uacute; Quốc như Cano SB c&oacute; m&aacute;i che, k&iacute;nh chắn gi&oacute; v&agrave; kh&ocirc;ng gian rộng r&atilde;i. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Kh&aacute;m ph&aacute; 4 đảo tại&nbsp;<strong>Ph&uacute; Quốc</strong>&nbsp;du kh&aacute;ch say đắm:&nbsp;</p>\r\n\r\n<p>- Chi&ecirc;m ngưỡng vẻ đẹp to&agrave;n cảnh từ c&aacute;p treo<strong>&nbsp;H&ograve;n Thơm</strong>, được tổ chức Guinness chứng nhận l&agrave; c&aacute;p treo d&agrave;i nhất thế giới.</p>\r\n\r\n<p>- Kh&aacute;m ph&aacute; 3 h&ograve;n đảo xinh đẹp gồm&nbsp;<strong>H&ograve;n Dăm Ngang, H&ograve;n M&acirc;y R&uacute;t Trong&nbsp;</strong>v&agrave;<strong>&nbsp;H&ograve;n M&acirc;y R&uacute;t Ngo&agrave;i.</strong></p>\r\n\r\n<p>-&nbsp;Thực đơn Hải Sản hấp dẫn.&nbsp;</p>\r\n', '<h3>Kh&aacute;m Ph&aacute; Vẻ Đẹp Hoang D&atilde; C&aacute;c Đảo Ph&uacute; Quốc</h3>\r\n\r\n<p>Nếu bạn muốn&nbsp;kh&aacute;m ph&aacute; một thi&ecirc;n đường tr&ecirc;n biển, sống động, đầy kỳ th&uacute; h&atilde;y đến với Ph&uacute; Quốc! Bạn sẽ được thỏa th&iacute;ch chi&ecirc;m ngưỡng vẻ đẹp thật k&igrave; diệu khi từ tr&ecirc;n cao nh&igrave;n xuống v&agrave; bao qu&aacute;t hết cả v&ugrave;ng trời biển ph&iacute;a Nam Ph&uacute; Quốc từ tr&ecirc;n ca bin c&aacute;p treo. C&aacute;p treo từ Cảng An Thới ra H&ograve;n Thơm Ph&uacute; Quốc với qu&atilde;ng đường d&agrave;i 7.899,9m, được Tổ chức Guinness trao tặng Chứng nhận C&aacute;p treo d&agrave;i nhất thế giới.&nbsp;Với lợi thế gồm c&aacute;c d&ograve;ng Cano mới v&agrave; hiện đại nhất Ph&uacute; Quốc như Cano SB c&oacute; m&aacute;i che, k&iacute;nh chắn gi&oacute; v&agrave; kh&ocirc;ng gian rộng r&atilde;i. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Kh&aacute;m ph&aacute; 4 đảo tại&nbsp;<strong>Ph&uacute; Quốc</strong>&nbsp;du kh&aacute;ch say đắm:&nbsp;</p>\r\n\r\n<p>- Chi&ecirc;m ngưỡng vẻ đẹp to&agrave;n cảnh từ c&aacute;p treo<strong>&nbsp;H&ograve;n Thơm</strong>, được tổ chức Guinness chứng nhận l&agrave; c&aacute;p treo d&agrave;i nhất thế giới.</p>\r\n\r\n<p>- Kh&aacute;m ph&aacute; 3 h&ograve;n đảo xinh đẹp gồm&nbsp;<strong>H&ograve;n Dăm Ngang, H&ograve;n M&acirc;y R&uacute;t Trong&nbsp;</strong>v&agrave;<strong>&nbsp;H&ograve;n M&acirc;y R&uacute;t Ngo&agrave;i.</strong></p>\r\n\r\n<p>-&nbsp;Thực đơn Hải Sản hấp dẫn.&nbsp;</p>\r\n\r\n<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<p>08h00: Xe đ&oacute;n du kh&aacute;ch tại resort tại thị trấn&nbsp;<strong>Dương Đ&ocirc;ng</strong>. Bắt đầu cuộc h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; quần đảo đẹp nhất ph&iacute;a nam&nbsp;<strong>Ph&uacute; Quốc.</strong></p>\r\n\r\n<p>09h00: Đến cảng ổn định chỗ ngồi v&agrave; khởi h&agrave;nh th&ocirc;i!</p>\r\n\r\n<p>C&ugrave;ng vi vu Quần&nbsp;<strong>Đảo An Thới&nbsp;</strong>bằng&nbsp;cano đi qua 12 đảo v&agrave; gh&eacute; tại 5 đảo đẹp nhất</p>\r\n\r\n<p>-&nbsp;<strong>C&ocirc;ng vi&ecirc;n San H&ocirc;</strong>: Kh&aacute;m ph&aacute; nhiều loại sinh vật biển với số lượng san h&ocirc; lớn nhất hiện nay.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/14/18/ivivu-lan-ngam-san-ho1.jpg\" /></p>\r\n\r\n<p><em>Lặn Ngắm San H&ocirc;.</em></p>\r\n\r\n<p>-&nbsp;<strong>H&ograve;n Dăm Ngang (H&ograve;n Gầm Gh&igrave;)</strong>: Trải nghiệm đắm m&igrave;nh trong l&agrave;n nước trong vắt v&agrave; lặn ngắm san h&ocirc; đầy sắc m&agrave;u.&nbsp;Được mệnh danh l&agrave; Vương Quốc San H&ocirc; &ndash; Lặn ngắm san h&ocirc;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/11/29/10/ivivu-hon-dam-ngang.jpg\" /></p>\r\n\r\n<p><em>H&ograve;n Dăm Ngang.</em></p>\r\n\r\n<p>-&nbsp;<strong>H&ograve;n M&acirc;y R&uacute;t Ngo&agrave;i</strong>&nbsp;v&agrave;&nbsp;<strong>H&ograve;n M&acirc;y R&uacute;t</strong>&nbsp;<strong>Trong:&nbsp;</strong>Qu&yacute; kh&aacute;ch tự do nghỉ ngơi &ndash; tắm biển hoặc kh&aacute;m ph&aacute; c&aacute;c hoạt động thể thao tr&ecirc;n b&atilde;i biển.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2021/05/05/11/ivivu-hon-may.jpg\" /></p>\r\n\r\n<p><em>H&ograve;n M&acirc;y R&uacute;t Trong.</em></p>\r\n\r\n<p>D&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>-&nbsp;<strong>H&ograve;n M&oacute;ng Tay</strong>: thư gi&atilde;n check in tại kh&ocirc;ng gian đẹp tựa thi&ecirc;n đường v&agrave; mệnh danh l&agrave; &ldquo; Madivies Việt &rdquo;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/11/07/14/ivivu-hon-mong-tay-pq.jpg\" /></p>\r\n\r\n<p><em>H&ograve;n M&oacute;ng Tay.</em></p>\r\n\r\n<p>14h00 &ndash; 14h30:&nbsp;<strong>B&atilde;i Tr&agrave;o H&ograve;n Thơm</strong>: Thỏa sức check in tại c&acirc;y cầu d&agrave;i bắt ra biển &ldquo; Cầu Ho&agrave;ng H&ocirc;n&rsquo;&rsquo;</p>\r\n\r\n<p>17h00:&nbsp;<strong>C&aacute;p treo h&ograve;n thơm</strong>: bạn sẽ được chi&ecirc;m ngưỡng to&agrave;n cảnh đẹp c&aacute;c đảo từ tr&ecirc;n cao.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/08/14/ivivu-cap-treo-hon-thom.gif\" /></p>\r\n\r\n<p><em>Cap Treo H&ograve;n Thơm.</em></p>\r\n\r\n<p>17h30: xe đ&oacute;n đo&agrave;n đưa về lại kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p><em>Thứ tự tham quan c&oacute; thể thay đổi nhưng vẫn đảm bảo đầy đủ điểm trong chương tr&igrave;nh.</em></p>\r\n', 1, 1),
(18, 'Tour Quy Nhơn 1N: Đảo Ngọc', '12345', '1230000', 22, 'Ảnh chụp Màn hình 2021-11-29 lúc 23.21.07.png', '<h3>C&ugrave; Lao Xanh - Thi&ecirc;n Đường Dưới Biển</h3>\r\n\r\n<p>Cũng c&oacute; c&aacute;t trắng, nắng v&agrave;ng, biển xanh, thế nhưng c&aacute;i t&ecirc;n C&ugrave; Lao Xanh cũng gợi l&ecirc;n ch&uacute;t g&igrave; đ&oacute; &ldquo;hoang sơ&rdquo; khiến nhiều người mới nghe th&ocirc;i cũng thấy th&egrave;m để được đến d&ugrave; chỉ một lần trong đời.&nbsp;Sự hoang sơ, thuần khiết v&agrave; thơ mộng, C&ugrave; Lao Xanh cũng khiến du kh&aacute;ch thập phương kh&ocirc;ng nỡ rời đi. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Tour trong ng&agrave;y</h3>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Kh&aacute;m Ph&aacute;&nbsp;<strong>C&ugrave; Lao Xanh</strong>&nbsp;&#39;H&uacute;t&#39; d&acirc;n du lịch:</p>\r\n\r\n<p>- Ngọn&nbsp;<strong>Hải đăng</strong>&nbsp;để ngắm trọn&nbsp;<strong>C&ugrave; Lao Xanh&nbsp;</strong>đ&acirc;y l&agrave; &ldquo;n&oacute;c nh&agrave;&rdquo; của C&ugrave; Lao Xanh, bạn dễ d&agrave;ng nh&igrave;n ngắm to&agrave;n cảnh C&ugrave; Lao Xanh Quy Nhơn xinh đẹp.</p>\r\n\r\n<p>-&nbsp;Những phiến đ&aacute; với mu&ocirc;n h&igrave;nh vạn trạng c&aacute;c kiểu đứng, ngồi, nằm,&hellip; mang m&agrave;u sắc tự nhi&ecirc;n đầy cuốn h&uacute;t được người d&acirc;n đặt t&ecirc;n cho l&agrave;&nbsp;<strong>B&atilde;i đ&aacute; thảo nguy&ecirc;n</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<p>7h00: Xe v&agrave; HDV&nbsp;c&ocirc;ng ty&nbsp;đ&oacute;n kh&aacute;ch tại điểm hẹn&nbsp;<strong>(trung t&acirc;m TP Quy Nhơn)</strong>&nbsp;khởi h&agrave;nh chuyến tham quan kh&aacute;m ph&aacute;<strong>&nbsp;C&ugrave; Lao Xanh</strong>. Qu&yacute; kh&aacute;ch l&ecirc;n cano tại bến&nbsp;<strong>H&agrave;m Tử</strong>, trải nghiệm cảm gi&aacute;c lướt tr&ecirc;n những con s&oacute;ng.</p>\r\n\r\n<p>8h30: Đến&nbsp;<strong>C&ugrave; Lao Xanh</strong>, Qu&yacute; kh&aacute;ch tắm biển v&agrave; lặn ngắm những rạn san h&ocirc; sống động nhiều m&agrave;u sắc tại&nbsp;<strong>B&atilde;i Nam, B&atilde;i Đ&aacute; H&ograve;n, B&atilde;i Nhỏ&nbsp;</strong>hoặc<strong>&nbsp;B&atilde;i biển Th&ocirc;n Đ&ocirc;ng.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/14/10/ivivu-cu-lao.gif\" /></strong></p>\r\n\r\n<p><em>C&ugrave; Lao Xanh.</em></p>\r\n\r\n<p>11h30: Qu&yacute; kh&aacute;ch thưởng thức bữa trưa với c&aacute;c m&oacute;n hải sản tươi sống: ốc biển, c&aacute; hấp, t&ocirc;m, mực, cầu gai&hellip;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch nghỉ trưa, thả m&igrave;nh v&agrave;o kh&ocirc;ng kh&iacute; trong l&agrave;nh m&aacute;t rượi của một&nbsp;<strong>C&ugrave; Lao Xanh</strong>&nbsp;hoang sơ, y&ecirc;n b&igrave;nh.</p>\r\n\r\n<p>13h30: Di chuyển đến l&agrave;ng ch&agrave;i tr&ecirc;n đảo. Qu&yacute; kh&aacute;ch tham quan ngọn&nbsp;<strong>Hải Đăng tr&ecirc;n 120</strong>&nbsp;năm tuổi. Đ&acirc;y được xem l&agrave; biểu tượng của ngư d&acirc;n x&atilde; đảo.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/14/10/ivivu-hai-dang-cu-lao-xanh.gif\" /></p>\r\n\r\n<p><em>Ngọn Hải Đăng.</em></p>\r\n\r\n<p>14h30: Tham quan&nbsp;<strong>B&atilde;i Đ&aacute; Thảo Nguy&ecirc;n</strong>, với mu&ocirc;n h&igrave;nh vạn trạng của đ&aacute; như được sắp đặt để tạo n&ecirc;n một khung cảnh đẹp mắt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/14/10/ivivu-bai-da-cu-lao-xanh.gif\" /></p>\r\n\r\n<p><em>B&atilde;i Đ&aacute; Thảo Nguy&ecirc;n.</em></p>\r\n\r\n<p>15h30: Qu&yacute; kh&aacute;ch l&ecirc;n cano khởi h&agrave;nh về&nbsp;<strong>Quy Nhơn</strong>. Xe đ&oacute;n qu&yacute; kh&aacute;ch tại&nbsp;<strong>Bến H&agrave;m Tử</strong>&nbsp;đưa Qu&yacute; kh&aacute;ch về điểm đ&oacute;n ban đầu.</p>\r\n\r\n<p>Kết th&uacute;c chương tr&igrave;nh. Th&acirc;n &aacute;i ch&agrave;o v&agrave; hẹn gặp lại.</p>\r\n\r\n<p><em>Thứ tự tham quan c&oacute; thể thay đổi nhưng vẫn đảm bảo đầy đủ điểm trong chương tr&igrave;nh.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Trải nghiệm theo c&aacute;ch của bạn</h3>\r\n', 1, 4),
(19, 'du lich đà nẵng', '12432', '5000000', 22, 'Ảnh chụp Màn hình 2021-11-29 lúc 23.28.23.png', '<h3><em><strong>3 ng&agrave;y 2 đ&ecirc;m</strong></em></h3>\r\n\r\n<h3><em><strong>xe kh&aacute;ch</strong></em></h3>\r\n\r\n<h3>Đ&agrave; Nẵng th&agrave;nh phố đ&aacute;ng sống nhất Việt Nam</h3>\r\n\r\n<p>Đ&agrave; Nẵng l&agrave; một trong 20 th&agrave;nh phố sạch nhất thế giới. Nằm ở v&ugrave;ng Nam Trung Bộ, Đ&agrave; Nẵng c&oacute; cả n&uacute;i, đồng bằng v&agrave; biển. C&aacute;c điểm tham quan du lịch nổi tiếng khi du lịch Đ&agrave; Nẵng bao gồm khu du lịch B&agrave; N&agrave;, b&atilde;i biển Mỹ Kh&ecirc;, v&agrave; khu vui chơi giải tr&iacute; trong nh&agrave; Fantasy Park lớn nhất ch&acirc;u &Aacute;. Đ&agrave; Nẵng c&ograve;n c&oacute; nhiều thắng cảnh m&ecirc; hồn như đ&egrave;o Hải V&acirc;n, rừng nguy&ecirc;n sinh ở b&aacute;n đảo Sơn Tr&agrave; v&agrave; Ngũ H&agrave;nh Sơn. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; Đ&agrave; Nẵng theo c&aacute;ch ri&ecirc;ng của m&igrave;nh ngay h&ocirc;m nay!!!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Đ&agrave; Nẵng nổi bật l&ecirc;n giữa miền Trung bởi...</p>\r\n\r\n<p>-&nbsp;<strong>Ngũ H&agrave;nh Sơn</strong>&nbsp;hay n&uacute;i<strong>&nbsp;Non Nước</strong>&nbsp;l&agrave; một thắng cảnh nổi tiếng, trong k&yacute; ức của nhiều người đ&acirc;y c&ograve;n l&agrave; v&ugrave;ng &ldquo;địa linh&rdquo; của xứ Quảng xưa nay.</p>\r\n\r\n<p>-&nbsp;<strong>Phố cổ Hội An</strong>&nbsp;như một bức tranh mộc mạc, giản dị v&agrave; n&ecirc;n thơ, từng l&agrave; một thương cảng quốc tế sầm uất, đ&atilde; được UNESCO c&ocirc;ng nhận l&agrave; di sản văn h&oacute;a thế giới năm 1999.</p>\r\n\r\n<p>-&nbsp;<strong>B&agrave; N&agrave; N&uacute;i Ch&uacute;a:</strong>&nbsp;khu nghỉ dưỡng tr&ecirc;n đỉnh n&uacute;i xinh đẹp như bao trọn to&agrave;n bộ c&aacute;c dịch vụ vui chơi, giải tr&iacute; cả nước nơi đ&acirc;y.</p>\r\n', '<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>NG&Agrave;Y 01: NGŨ H&Agrave;NH SƠN&nbsp;&ndash; HỘI AN (ĂN TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng: Xe v&agrave; HDV đ&oacute;n qu&yacute; kh&aacute;ch tại&nbsp;<strong>s&acirc;n bay/ga Đ&agrave; Nẵng</strong>&nbsp;trước 12h00.&nbsp;</p>\r\n\r\n<p>Xe đưa Qu&yacute; kh&aacute;ch d&ugrave;ng bữa trưa. Nhận ph&ograve;ng Kh&aacute;ch sạn, nghỉ ngơi.</p>\r\n\r\n<p><strong>Chiều: 15h00:</strong>&nbsp;Khởi h&agrave;nh&nbsp;đi&nbsp;<strong>Ngũ H&agrave;nh Sơn</strong>&nbsp;- c&aacute;ch trung t&acirc;m 10km với 5 ngọn n&uacute;i được nằm theo hệ Ngũ H&agrave;nh. Qu&yacute; kh&aacute;ch sẽ tham quan:&nbsp;<strong>Th&aacute;p X&aacute; Lợi, Ch&ugrave;a Linh Ứng 1, động T&agrave;ng Chơn, động V&acirc;n Th&ocirc;ng, Huyền Kh&ocirc;ng quan, Vọng Giang Đ&agrave;i, Ch&ugrave;a Tam Thai</strong>&nbsp;sau đ&oacute; hạ sơn bằng đường bộ.</p>\r\n\r\n<p><em><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/27/09/ivivu-ngu-hanh-son-dn.gif\" /></em></p>\r\n\r\n<p><em>Ngũ H&agrave;nh Sơn.</em></p>\r\n\r\n<p>- Tiếp tục thăm&nbsp;<strong>L&agrave;ng đ&aacute; Mỹ nghệ Non Nước</strong>&nbsp;với c&aacute;c t&aacute;c phẩm nghệ thuật được tạo n&ecirc;n bởi b&agrave;n tay t&agrave;i hoa của c&aacute;c nghệ nh&acirc;n l&agrave;ng nghề cổ 300 năm tuổi.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/27/09/ivivu-lang-da-my-nghe-non-nuoc.gif\" /></p>\r\n\r\n<p><em>L&agrave;ng đ&aacute; Mỹ nghệ Non Nước.</em></p>\r\n\r\n<p>- Xe v&agrave; HDV tiếp tục đưa đo&agrave;n tham quan&nbsp;<strong>phố cổ Hội An</strong>. Qu&yacute; kh&aacute;ch sẽ c&oacute; dịp tham quan&nbsp;<strong>ch&ugrave;a Cầu</strong>&nbsp;<strong>Nhật Bản</strong>, x&acirc;y dựng v&agrave;o đầu thế kỷ 17, một biểu tượng độc đ&aacute;o v&agrave; rất th&acirc;n thương của người Hội An. Sau đ&oacute;, qu&yacute; kh&aacute;ch sẽ thăm hai trong c&aacute;c điểm sau:&nbsp;<strong>Hội Qu&aacute;n Quảng Đ&ocirc;ng, Hội Qu&aacute;n Ph&uacute;c Kiến, nh&agrave; cổ Tấn K&yacute;, nh&agrave; cổ Ph&ugrave;ng Hưng</strong>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-chua-cau-hoi-an.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Hội An.</em></p>\r\n\r\n<p><strong>Tối:&nbsp;</strong>Đo&agrave;n d&ugrave;ng bữa tại nh&agrave; h&agrave;ng, thưởng thức c&aacute;c&nbsp;<strong>đặc sản tại</strong>&nbsp;<strong>Hội An&nbsp;</strong>(Cao lầu, ho&agrave;nh th&aacute;nh, cơm g&agrave;...).&nbsp;</p>\r\n\r\n<p>Xe v&agrave; HDV sẽ đưa đo&agrave;n về&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;theo cung đường ven biển. Qu&yacute; kh&aacute;ch c&oacute; dịp ngắm nh&igrave;n c&aacute;c resort, s&acirc;n g&ocirc;n cao cấp từ Hội An ra Đ&agrave; Nẵng. Nhận ph&ograve;ng kh&aacute;ch sạn 4 sao nghỉ ngơi.</p>\r\n\r\n<h3>NG&Agrave;Y 02:&nbsp;B&Agrave; N&Agrave; HILLS&nbsp; (ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng:&nbsp;Điểm t&acirc;m s&aacute;ng.</p>\r\n\r\n<p>7h30: Khởi h&agrave;nh đến với&nbsp;khu du lịch&nbsp;<strong>B&agrave; N&agrave; Hills,</strong>&nbsp;nơi Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; những khoảnh khắc giao m&ugrave;a bất ngờ Xu&acirc;n &ndash; Hạ &ndash; Thu &ndash; Đ&ocirc;ng trong 1 ng&agrave;y.</p>\r\n\r\n<p>8h30:&nbsp;Đến&nbsp;<strong>Ga Suối Mơ</strong>, tại đ&acirc;y Qu&yacute; kh&aacute;ch tự do chụp ảnh lưu niệm. Sau đ&oacute;, Qu&yacute; kh&aacute;ch l&ecirc;n c&aacute;p treo&nbsp;<strong>B&agrave; N&agrave;</strong>&nbsp;&ndash; đạt 2 kỷ lục Guinness thế giới.&nbsp;Ngồi trong c&aacute;p treo, qu&yacute; kh&aacute;ch c&ograve;n c&oacute; thể thu v&agrave;o tầm mắt to&agrave;n cảnh&nbsp;<strong>Đ&agrave; Nẵng</strong>, từ vịnh biển&nbsp;<strong>Nguyễn Tất Th&agrave;nh</strong>&nbsp;đến b&aacute;n đảo&nbsp;<strong>Sơn Tr&agrave;, đ&egrave;o Hải V&acirc;n</strong>&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/27/09/ivivu-cap-treo-ba-na-dn.gif\" /></p>\r\n\r\n<p><em>C&aacute;p Treo B&agrave; N&agrave;.</em></p>\r\n\r\n<p>09h00:&nbsp;Đến&nbsp;<strong>Ga B&agrave; N&agrave;</strong>, qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>B&agrave; N&agrave; Bynight</strong>&nbsp;với hầm rượu 100 năm tuổi, tại đ&acirc;y Qu&yacute; kh&aacute;ch c&oacute; thể thưởng thức 1 ly rượu vang Ph&aacute;p sẽ l&agrave;m ấm l&ograve;ng khi đi dạo ở nơi se lạnh như&nbsp;<strong>B&agrave; N&agrave;</strong>&nbsp;(chi ph&iacute; tự t&uacute;c).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/14/15/ivivu-ba-na.jpg\" /></p>\r\n\r\n<p><em>B&agrave; N&agrave;.</em></p>\r\n\r\n<p>Tham quan&nbsp;<strong>Cầu V&agrave;ng b&agrave;n tay</strong>,&nbsp;<strong>Đồi Vọng Nguyệt, ch&ugrave;a Linh Ứng, Th&iacute;ch Ca Phật Đ&agrave;i</strong>, khu chuồng ngựa cũ của Ph&aacute;p,&nbsp;<strong>Vườn Tịnh T&acirc;m</strong>&nbsp;v&agrave; đỉnh nh&agrave; r&ocirc;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/04/10/ivivu-ba-na-nui-chua.jpg\" /></p>\r\n\r\n<p>&nbsp;<em>Cầu V&agrave;ng B&agrave;n Tay.</em></p>\r\n\r\n<p>Tiếp tục ngồi c&aacute;p treo đến đỉnh&nbsp;<strong>Nghinh Phong</strong>, biệt thự&nbsp;<strong>Lệ Nim, Lầu Vọng Nguyệt, Cầu Treo B&agrave; N&agrave;</strong>&nbsp;v&agrave; chinh phục đỉnh n&uacute;i Ch&uacute;a. Tiếp tục tham quan Khu vui chơi giải tr&iacute; trong nh&agrave; lớn thứ 3 Thế giới với t&ecirc;n gọi l&agrave;&nbsp;<strong>FANTASY PARK</strong>&nbsp;(V&ograve;ng quay t&igrave;nh y&ecirc;u, phi c&ocirc;ng SKIVER, đường đua lửa, cối xay gi&oacute;, người nhện, th&aacute;p rơi xoay tự do, Xem phim 4D, C&ocirc;ng vi&ecirc;n khủng long), (Bảo t&agrave;ng tượng s&aacute;p&hellip;chi ph&iacute; tr&ograve; chơi tự t&uacute;c).&nbsp;</p>\r\n\r\n<p><em><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/27/10/ivivu-fantasy-park-dn.gif\" /></em></p>\r\n\r\n<p><em>FANTASY PARK.</em></p>\r\n\r\n<p>Trưa:&nbsp;Ăn trưa buffet tại nh&agrave; h&agrave;ng tr&ecirc;n&nbsp;<strong>B&agrave; N&agrave;</strong>. Rời&nbsp;<strong>B&agrave; N&agrave;</strong>&nbsp;về lại&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;theo tuyến đường du lịch&nbsp;<strong>Nguyễn Tất Th&agrave;nh</strong>, tuyến đường biển đẹp nhất Miền Trung,&nbsp;gh&eacute; tham quan mua sắm tại si&ecirc;u thị đặc sản miền Trung.</p>\r\n\r\n<p>Tối:&nbsp;D&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng&nbsp;hoặc&nbsp;<strong>Cơm G&agrave;</strong>&nbsp;nổi tiếng&nbsp;<strong>Đ&agrave; Nẵng.&nbsp;</strong>Kết th&uacute;c bữa tối, xe v&agrave; hdv đưa Q&uacute;y kh&aacute;ch về lại kh&aacute;ch sạn.</p>\r\n\r\n<p>Tối:&nbsp;Qu&yacute; kh&aacute;ch được tự do kh&aacute;m ph&aacute;&nbsp;<strong>Phố Biển Đ&agrave; Nẵng</strong>&nbsp;về đ&ecirc;m: Ngồi Du thuyền s&ocirc;ng H&agrave;n hoặc V&ograve;ng quay&nbsp;<strong>Mặt Trời Sun Whel</strong>l ngắm cảnh TP (chi ph&iacute; tự t&uacute;c), Trung T&acirc;m Thương Mại, Khu phố ẩm thực,&nbsp;<strong>Caf&eacute; - Bar &ndash; Discotheque</strong>,&hellip;&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/27/10/ivivu-sunwheel.gif\" /></p>\r\n\r\n<p><em>Mặt Trời Sun Whell.</em></p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;kh&aacute;ch sạn 4 sao.</p>\r\n\r\n<h3>NG&Agrave;Y 03: Đ&Agrave; NẴNG CITY&nbsp; (ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m. Khởi h&agrave;nh đi&nbsp;<strong>B&aacute;n Đảo Sơn Tr&agrave; (Monkey Moutain).&nbsp;</strong>Qu&yacute; kh&aacute;ch Viếng&nbsp;<strong>ch&ugrave;a Linh Ứng</strong>, chi&ecirc;m ngưỡng&nbsp;<strong>Tượng Phật Quan Thế &Acirc;m Bồ T&aacute;t</strong>&nbsp;cao nhất Việt Nam 67m.Thưởng ngoạn vẻ đẹp v&agrave; tắm biển Mỹ Kh&ecirc; (Được tạp ch&iacute; Forbes b&igrave;nh chọn l&agrave; 1 trong 6 b&atilde;i biển quyến rũ nhất H&agrave;nh Tinh).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-chua-linh-ung.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Linh Ứng B&aacute;n Đảo Sơn Tr&agrave;.</em></p>\r\n\r\n<p>Xe v&agrave; Hướng dẫn sẽ đưa Q&uacute;y kh&aacute;ch dạo quanh, ngắm nh&igrave;n v&agrave; nghe hướng dẫn thuyết minh về những điểm nhấn kiến tr&uacute;c nổi tiếng của Th&agrave;nh Phố Du Lịch&nbsp;<strong>Đ&agrave; Nẵng</strong>:&nbsp;<strong>T&ograve;a nh&agrave; h&agrave;nh ch&iacute;nh,&nbsp;Cầu Quay s&ocirc;ng H&agrave;n, Cầu rồng</strong>: tản bộ h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh b&ecirc;n bờ s&ocirc;ng H&agrave;n, tham quan v&agrave; chụp h&igrave;nh lưu lại dấu ấn kh&oacute; qu&ecirc;n tại&nbsp;Cầu T&igrave;nh y&ecirc;u, chi&ecirc;m ngưỡng tượng&nbsp;&quot;C&aacute; ch&eacute;p h&oacute;a rồng&quot;&nbsp; &hellip;Mua sắm qu&agrave; tại&nbsp;si&ecirc;u thị đặc sản miền Trung hoặc chợ H&agrave;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-cau-rong.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Cầu Rồng.</em></p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng với đặc sản nổi tiếng Đ&agrave; Nẵng&nbsp;&ldquo;<strong>B&aacute;nh tr&aacute;ng thịt heo 2 đầu da &amp; Mỳ Quảng</strong>&rdquo;.</p>\r\n\r\n<p><strong>Chiều:&nbsp;</strong>Xe tiễn kh&aacute;ch ra&nbsp;<strong>S&acirc;n bay Đ&agrave; Nẵng&nbsp;</strong>(c&aacute;c&nbsp;<strong>chuyến bay từ 14h00</strong>). HDV ch&agrave;o tạm biệt du kh&aacute;ch, kết th&uacute;c chương tr&igrave;nh tham quan th&uacute; vị tại đ&acirc;y.</p>\r\n\r\n<p><em>Thứ tự c&aacute;c điểm tham quan c&oacute; thể thay đổi nhưng vẫn đảm bảo đầy đủ điểm trong chương tr&igrave;nh.</em></p>\r\n', 0, 1),
(20, 'Nha Trang (3N3Đ)', '33452', '7000000', 11, 'Ảnh chụp Màn hình 2021-11-29 lúc 23.55.50.png', '<ul>\r\n	<li><strong>VẬN CHUYỂN:</strong>&nbsp;Xe du lịch 16 - 45 chỗ (ph&ugrave; hợp số lượng) đời mới m&aacute;y lạnh, ghế bật.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\r\n	<li><strong>KH&Aacute;CH SẠN:&nbsp;</strong>Ti&ecirc;u chuẩn 02-03 kh&aacute;ch/ph&ograve;ng. Tiện nghi: m&aacute;y lạnh, tivi, nước&nbsp;n&oacute;ng, vệ sinh<br />\r\n	&nbsp; &nbsp; &nbsp;<strong>+</strong>&nbsp;<strong>3 sao: Edele,&hellip;hoặc tương đương</strong><br />\r\n	&nbsp; &nbsp;&nbsp;<strong>&nbsp;+&nbsp;4 sao: Apus, Erica Hotel,... hoặc tương đương</strong><br />\r\n	<strong>&nbsp; &nbsp; &nbsp;+</strong>&nbsp;<strong>5 sao: Virgo&nbsp;Hotel&hellip; hoặc tương đương</strong></li>\r\n	<li><strong>ĂN UỐNG:<br />\r\n	&nbsp; &nbsp; &nbsp;</strong>+<strong>&nbsp;</strong>Ăn s&aacute;ng tại nh&agrave; h&agrave;ng..<br />\r\n	&nbsp; &nbsp; &nbsp;+ Ăn trưa, chiều: tại nh&agrave; h&agrave;ng địa phương ti&ecirc;u chuẩn, hợp vệ sinh.</li>\r\n	<li><strong>HDV:&nbsp;</strong>Đo&agrave;n c&oacute; HDV tiếng Việt thuyết minh v&agrave; phục vụ tham quan su&ocirc;́t tuyến.</li>\r\n	<li><strong>BẢO HIỂM:&nbsp;</strong>Bảo hiểm theo quy định của Bảo hiểm du lịch 100.000.000 vnđ/vụ.</li>\r\n	<li><strong>THAM QUAN:</strong>&nbsp;Gi&aacute; tour bao gồm ph&iacute; v&agrave;o cổng tại c&aacute;c điểm tham quan theo chương tr&igrave;nh.</li>\r\n	<li><strong>QU&Agrave; TẶNG:&nbsp;</strong>N&oacute;n du lịch&nbsp; Đất Việt, Khăn ướt + nước đ&oacute;ng chai.</li>\r\n</ul>\r\n', '<p>TPHCM - KH&Aacute;NH H&Ograve;A</p>\r\n\r\n<p><img src=\"https://datviettour.com.vn/uploads/images/mien-trung/nha-trang/hinh-noi-dung-tour/nha-trang-1.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;20h00, Xe v&agrave; Hướng dẫn vi&ecirc;n của&nbsp;<strong>DU LỊCH ĐẤT VIỆT</strong>&nbsp;đ&oacute;n qu&yacute; kh&aacute;ch tại điểm hẹn, sắp xếp h&agrave;nh l&yacute;, Xe khởi h&agrave;nh, bắt đầu h&agrave;nh tr&igrave;nh&nbsp;<strong>Tour Du Lịch NHA TRANG &ndash; CON SẺ TRE&nbsp;&ndash; THI&Ecirc;N ĐƯỜNG BIỂN 3 ng&agrave;y 3 đ&ecirc;m.</strong>&nbsp;Qu&yacute; kh&aacute;ch nghỉ ngơi tr&ecirc;n xe.</p>\r\n\r\n<p>Tr&ecirc;n đường đi, HDV tổ chức hoạt n&aacute;o, gắn kết c&aacute;c th&agrave;nh vi&ecirc;n đ&atilde; tham gia&nbsp;<strong>NG&Ocirc;I NH&Agrave; CHUNG ĐẤT VIỆT</strong>. Qu&yacute; kh&aacute;ch nghỉ ngơi tự do tr&ecirc;n xe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y 1</p>\r\n\r\n<p>BIỂN B&Atilde;I D&Agrave;I - CITY TOUR NHA TRANG ( &bull; Ăn s&aacute;ng &bull; Ăn trưa &bull; Ăn tối )</p>\r\n\r\n<p><img src=\"https://datviettour.com.vn/uploads/images/mien-trung/nha-trang/hinh-noi-dung-tour/nha-trang-thap-ba-chua-long-son-lang-yen.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Khi b&igrave;nh m&igrave;nh vừa đến, Đo&agrave;n cũng c&oacute; mặt tại Cam Ranh. Qu&yacute; kh&aacute;ch vệ sinh c&aacute; nh&acirc;n. D&ugrave;ng điểm t&acirc;m s&aacute;ng tại nh&agrave; h&agrave;ng. Nghỉ ngơi sau chặng di chuyển d&agrave;i. Đo&agrave;n tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>B&Atilde;I D&Agrave;I:&nbsp;</strong>Với b&atilde;i c&aacute;t d&agrave;i v&agrave; mịn m&agrave;ng, khi thủy triều r&uacute;t, du kh&aacute;ch sẽ kh&ocirc;ng khỏi ngạc nhi&ecirc;n khi thấy hiện ra trước mắt l&agrave; cả một bờ c&aacute;t trắng trải d&agrave;i m&ecirc;nh m&ocirc;ng. V&agrave;o buổi s&aacute;ng mặt nước b&atilde;i d&agrave;i trong vắt như một tấm thủy tinh phản chiếu. Cả bầu trời như nằm gọn trong l&ograve;ng biển. Qu&yacute; kh&aacute;ch nhận ghế lều c&ugrave;ng sinh hoạt v&agrave; tắm biển tự do, thưởng thức hải sản tươi sống, tham gia c&aacute;c m&ocirc;n thể thao tr&ecirc;n biển như: Ch&egrave;o thuyền Kayak, M&ocirc; t&ocirc; nước&hellip;&nbsp;<strong>(Chi ph&iacute; tự t&uacute;c)</strong></li>\r\n</ul>\r\n\r\n<p><strong>Trưa:&nbsp;</strong>Qu&yacute; kh&aacute;ch đến với&nbsp;<strong>th&agrave;nh phố biển NHA TRANG</strong>&nbsp;d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng. Đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng v&agrave; nghỉ ngơi tự do.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Qu&yacute; kh&aacute;ch tham quan</p>\r\n\r\n<ul>\r\n	<li><strong>CH&Ugrave;A LONG SƠN:&nbsp;</strong>&ldquo;Ai về ngắm cảnh Kh&aacute;nh Ho&agrave;/Long Sơn n&ecirc;n gh&eacute;/Th&aacute;p B&agrave; đừng qu&ecirc;n/Kim th&acirc;n Phật tổ nhớ l&ecirc;n/Nh&igrave;n &ocirc;ng Phật trắng ngồi tr&ecirc;n lưng trời&rdquo; - C&acirc;u ca dao len nhẹ l&ograve;ng người, dẫn dắt bước ch&acirc;n du kh&aacute;ch h&agrave;nh hương l&ecirc;n đồi Trại Thủy v&atilde;n cảnh ch&ugrave;a, chi&ecirc;m b&aacute;i Kim th&acirc;n Phật tổ v&agrave; ngắm nh&igrave;n Nha Trang vươn d&agrave;i theo m&eacute;p biển.</li>\r\n	<li><strong>TH&Aacute;P B&Agrave; PONAGAR:</strong>&nbsp;được coi l&agrave; biểu tượng của Nha Trang, l&agrave; quần thể kiến tr&uacute;c Chăm Pa thuộc h&agrave;ng quy&nbsp;m&ocirc; nhất ở khu vực miền Trung. Mặc d&ugrave; thời gian đ&atilde; l&agrave;m to&agrave;n bộ khu di t&iacute;ch trở n&ecirc;n cũ kĩ r&ecirc;u phong nhưng vẫn l&agrave; một c&ocirc;ng tr&igrave;nh nghệ thuật kiến tr&uacute;c v&agrave; đi&ecirc;u khắc độc đ&aacute;o của người Chăm cổ.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong>L&Agrave;NG YẾN MAI SINH NHA TRANG</strong>&nbsp;đang l&agrave; một trong những địa điểm đầy hấp dẫn v&agrave; th&uacute; vị. Đ&acirc;y l&agrave; điểm đến m&agrave; du kh&aacute;ch sẽ kh&aacute;m ph&aacute; những gi&aacute; trị trường tồn m&agrave; chim yến đ&atilde; mang lại. Tại đ&acirc;y, Qu&yacute; kh&aacute;ch được:</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Kh&aacute;m ph&aacute; m&ocirc; h&igrave;nh hang động Chim Yến</li>\r\n	<li>Sống ảo với g&oacute;c phố &ocirc; d&ugrave; &ldquo;Agueda&rdquo; c&ugrave;ng những bức tường m&agrave;u huyền thoại</li>\r\n	<li>Mua sắm tại khu t&iacute;ch hợp thương mại Yến s&agrave;o</li>\r\n	<li>Thưởng thức coffee &ndash; điểm t&acirc;m theo phong c&aacute;ch &ldquo;Riverside&rdquo;&nbsp;<strong>(chi ph&iacute; tự t&uacute;c)</strong></li>\r\n</ul>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Xe đưa Qu&yacute; kh&aacute;ch đến nh&agrave; h&agrave;ng d&ugrave;ng cơm chiều.</p>\r\n\r\n<p>Tại&nbsp;<strong>th&agrave;nh phố Nha Trang</strong>, từ l&acirc;u đ&atilde; nổi tiếng với c&aacute;c một loạt c&aacute;c hoạt động về đ&ecirc;m n&iacute;u giữ du kh&aacute;ch đến đ&acirc;y. Bạn c&oacute; thể&nbsp;<strong>tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh của biển cả ven đường TRẦN PH&Uacute;,</strong>&nbsp;hoặc v&ocirc; tư mua sắm tại c&aacute;c cung đường khu&nbsp;<strong>CHỢ Đ&Ecirc;M</strong>, hay thả hồn du dương theo tiếng nhạc của c&aacute;c bạn nghệ sĩ đường phố. Tất cả tạo n&ecirc;n một bức tranh về&nbsp;<strong>TH&Agrave;NH PHỐ N&Aacute;O NHI&Ecirc;T</strong>&nbsp;hấp dẫn du kh&aacute;ch. Qu&yacute; kh&aacute;ch nghỉ ngơi tự do.</p>\r\n', 1, 5),
(21, 'Tp Hồ Chí Minh Cần Giờ (1 N)', '2231', '1300000', 11, 'Ảnh chụp Màn hình 2021-11-30 lúc 00.05.26.png', '<h3><strong>GI&Aacute; TOUR BAO GỒM&nbsp;</strong></h3>\r\n\r\n<ul>\r\n	<li>Vận chuyển: Xe du lịch 16 - 45 chỗ đời mới m&aacute;y lạnh, tivi, ghế bật.</li>\r\n	<li>Ăn uống: Ăn s&aacute;ng TR&Ecirc;N XE (B&Aacute;NH M&Igrave;/B&Aacute;NH BAO/SỮA) + Ăn trưa tại nh&agrave; h&agrave;ng địa phương hợp vệ sinh. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;</li>\r\n	<li>HDV: Đo&agrave;n c&oacute; HDV tiếng Việt thuyết minh v&agrave; phục vụ &nbsp;tham quan su&ocirc;́t tuyến.</li>\r\n	<li>Bảo hiểm: Bảo hiểm du lịch theo quy định 40.000.000vnđ/vụ</li>\r\n	<li>Tham quan: Gi&aacute; tour đ&atilde; bao gồm ph&iacute; v&agrave;o cổng tại c&aacute;c điểm tham quan &amp; ph&iacute; đi Ca n&ocirc;</li>\r\n	<li>Q&ugrave;a tặng: N&oacute;n du lịch &nbsp;Đất Việt, Khăn ướt + nước đ&oacute;ng chai ( 02 Chai/ng&agrave;y).</li>\r\n</ul>\r\n\r\n<h3><strong>GI&Aacute; TOUR KH&Ocirc;NG BAO GỒM</strong></h3>\r\n\r\n<ul>\r\n	<li>Tiền TIP cho HDV + T&agrave;i xế địa phương , VAT</li>\r\n	<li>Chi ph&iacute; c&aacute; nh&acirc;n ngo&agrave;i chương tr&igrave;nh: Giặt ủi, điện thoại, minibar&hellip;</li>\r\n</ul>\r\n\r\n<h3>&nbsp;</h3>\r\n', '<p>S&Agrave;I G&Ograve;N - RỪNG S&Aacute;C CẦN GIỜ ( &bull; Ăn s&aacute;ng &bull; Ăn trưa )</p>\r\n\r\n<p><img src=\"https://datviettour.com.vn/uploads/images/mien-nam/can-gio/hinh-noi-dung-tour/can-gio-vam-sat-2n1d-1.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>S&aacute;ng:</strong></p>\r\n\r\n<p>Xe v&agrave; HDV sẽ đ&oacute;n qu&yacute; kh&aacute;ch tại điểm hẹn v&agrave; kiểm tra nhanh, đảm bảo an to&agrave;n cho tất cả du kh&aacute;ch. Sau đ&oacute; khởi h&agrave;nh bắt đầu h&agrave;nh tr&igrave;nh &ldquo;du lịch xanh - an to&agrave;n đi&nbsp;<a href=\"https://datviettour.com.vn/tour-du-lich-can-gio\" target=\"_blank\"><strong>Cần Giờ</strong></a>&nbsp;- được mệnh danh l&agrave; &ldquo;l&aacute; phổi xanh&rdquo; của th&agrave;nh phố mang t&ecirc;n B&aacute;c.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch Ăn s&aacute;ng tr&ecirc;n xe với b&aacute;nh m&igrave;/banh bao + sữa hộp</p>\r\n\r\n<p>Tr&ecirc;n xe qu&yacute; kh&aacute;ch tham gia những hoạt động vui nhộn, đặc sắc, do HDV tổ chức&hellip; Hướng dẫn vi&ecirc;n sẽ chia sẻ c&ugrave;ng gi&uacute;p qu&yacute; kh&aacute;ch t&igrave;m hiểu th&ecirc;m về lịch sử, văn ho&aacute;, phong tục tập qu&aacute;n v&agrave; con người từng v&ugrave;ng đất m&agrave; đo&agrave;n ngang qua.</p>\r\n\r\n<p>Đến&nbsp;<strong>KDL L&Acirc;M VI&Ecirc;N CẦN GIỜ</strong>&nbsp;đo&agrave;n tham quan &ldquo;<strong>Đảo khỉ hoang d&atilde;</strong>&rdquo; (khỉ đu&ocirc;i d&agrave;i, đ&acirc;y l&agrave; lo&agrave;i khỉ đặc trưng tại Khu dự trữ sinh quyển Rừng ngập mặn Cần Giờ) với hơn 1000 con khỉ.</p>\r\n\r\n<p>Tham quan Trại c&aacute; sấu Hoa C&agrave;. Đi t&agrave;u gỗ ngắm cảnh quan s&ocirc;ng nước Rừng ngập mặn Cần Giờ (thời gian đi t&agrave;u 60 ph&uacute;t).</p>\r\n\r\n<p><strong>Trưa:</strong></p>\r\n\r\n<p>Đo&agrave;n d&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng. Tự do nghỉ ngơi.</p>\r\n\r\n<p>Sau đ&oacute; đo&agrave;n di chuyển đến KHU DU LỊCH SINH TH&Aacute;I DẦN X&Acirc;Y. Đo&agrave;n l&ecirc;n ca n&ocirc; qua Khu bảo tồn đ&agrave;n Dơi Nghệ: tại đ&acirc;y nh&acirc;n vi&ecirc;n ch&egrave;o xuồng tay sẽ đưa Qu&yacute; kh&aacute;ch len lỏi v&agrave;o rừng Đước trong đầm, Qu&yacute; kh&aacute;ch sẽ được ngắm nh&igrave;n một trong những giống dơi qu&yacute; hiếm nhất thế giới đang treo ngược m&igrave;nh tr&ecirc;n c&agrave;nh c&acirc;y v&agrave; tự do h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh, tho&aacute;ng m&aacute;t của v&ugrave;ng ngập mặn Cần Giờ.</p>\r\n\r\n<p><strong>Chiều:</strong></p>\r\n\r\n<p>Đo&agrave;n l&ecirc;n ca n&ocirc; trở lại KDL Dần X&acirc;y. Đo&agrave;n len lỏi qua con đường b&ecirc; t&ocirc;ng xuy&ecirc;n rừng v&agrave; được nghe giới thiệu về qu&aacute; tr&igrave;nh kh&ocirc;i phục v&agrave; ph&aacute;t triển hệ sinh th&aacute;i rừng ngập mặn Cần Giờ</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch l&ecirc;n xe khởi h&agrave;nh về trung t&acirc;m TP. Hồ Ch&iacute; Minh. Đến điểm đ&oacute;n ban đầu, kết th&uacute;c chương tr&igrave;nh&nbsp;<a href=\"https://datviettour.com.vn/tour-1-ngay\" target=\"_blank\"><strong>tour 1 ng&agrave;y</strong></a>&nbsp;nhiều trải nghiệm.</p>\r\n', 1, 5);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`) VALUES
(22, 'Sa Pa', '21323', '4000000', 22, 'sp.jpeg', '<h3>Chốn Bồng Lai Ti&ecirc;n Cảnh V&ugrave;ng T&acirc;y Bắc</h3>\r\n\r\n<p>Y T&yacute; kh&ocirc;ng c&ograve;n l&agrave; c&aacute;i t&ecirc;n xa lạ đối với những người y&ecirc;u th&iacute;ch x&ecirc; dịch, đặc biệt l&agrave; giới trẻ v&agrave; du kh&aacute;ch từ phương Nam. C&aacute;ch SaPa kh&ocirc;ng đầy 70km, nhiều người đ&atilde; mến gọi địa danh n&agrave;y bằng c&aacute;i t&ecirc;n &ldquo;chốn bồng lai ti&ecirc;n cảnh&rdquo; bởi vẻ lung linh huyền ảo như chốn thần ti&ecirc;n.&nbsp;Nơi đ&acirc;y, lu&ocirc;n phong ph&uacute; v&agrave; đầy ắp c&aacute;c n&eacute;t văn h&oacute;a độc đ&aacute;o, v&agrave; cơ man n&agrave;o l&agrave; đặc sản độc lạ đầy th&uacute; vị của c&aacute;c d&acirc;n tộc H&rsquo;Mong, Dao, người Gi&aacute;y,&hellip; khiến bạn kh&ocirc;ng thể n&agrave;o qu&ecirc;n. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n', '<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>H&agrave;nh Tr&igrave;nh Từ Đ&ocirc;ng Bắc sang T&acirc;y Bắc, qu&yacute; kh&aacute;ch bắt trọn từng khoảng khắc sống ảo cực đẹp:&nbsp;</p>\r\n\r\n<p>- Ngắm&nbsp;ruộng bậc thang tại&nbsp;<strong>Bản Ph&ugrave;ng</strong>&nbsp;- được c&ocirc;ng nhận l&agrave; danh thắng di t&iacute;ch&nbsp;quốc gia.</p>\r\n\r\n<p>- Check in ruộng bậc thang tại Th&ocirc;ng Nguy&ecirc;n, cổng trời&nbsp;<strong>Su Ph&igrave;</strong></p>\r\n\r\n<p>- Săn m&acirc;y tại thung lũng&nbsp;<strong>Thể Pả - Y T&yacute;</strong></p>\r\n\r\n<p>- Tham quan khu du lịch&nbsp;<strong>Sapa</strong>&nbsp;- nơi m&agrave; người Ph&aacute;p xưa gọi l&agrave; &ldquo;kinh đ&ocirc; m&ugrave;a h&egrave; của xứ Bắc Kỳ&rdquo;.</p>\r\n\r\n<p>- Chinh phục đỉnh&nbsp;<strong>Fansipan</strong>&nbsp;bằng hệ thống c&aacute;p treo lớn v&agrave; hiện đại nhất Thế Giới.</p>\r\n\r\n<p>- Tham quan&nbsp;<strong>Bản C&aacute;t C&aacute;t</strong>&nbsp;- địa b&agrave;n cư tr&uacute; của người H&#39;M&ocirc;ng, ngắm cảnh h&ugrave;ng vĩ của n&uacute;i rừng T&acirc;y Bắc</p>\r\n\r\n<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>NG&Agrave;Y 01: TP. HCM &ndash; H&Agrave; NỘI &ndash; HO&Agrave;NG SU PH&Igrave; &ndash; BẢN PH&Ugrave;NG &ndash; NẬM HỒNG &ndash; TH&Ocirc;NG NGUY&Ecirc;N (ĂN TRƯA, TỐI)&nbsp;</h3>\r\n\r\n<p>Buổi s&aacute;ng: 05h00:&nbsp;Hướng dẫn vi&ecirc;n l&agrave;m thủ tục cho qu&yacute; kh&aacute;ch đ&aacute;p chuyến bay (SGN-HAN) cất c&aacute;nh l&uacute;c 07h00&rsquo;.&nbsp;</p>\r\n\r\n<p>09h00&rsquo;: M&aacute;y bay hạ c&aacute;nh tại s&acirc;n bay&nbsp;<strong>Nội B&agrave;i</strong>,</p>\r\n\r\n<p>Xe v&agrave; HDV đ&oacute;n Qu&yacute; kh&aacute;ch tại điểm hẹn khởi h&agrave;nh đi&nbsp;<strong>Ho&agrave;ng Su Ph&igrave;</strong>&nbsp;theo quốc lộ 2 - điểm đến được y&ecirc;u th&iacute;ch những năm gần đ&acirc;y bởi n&eacute;t hoang sơ, t&aacute;ch biệt ồn &agrave;o nơi phố thị, chưa hề bị du lịch h&oacute;a, mang lại cảm gi&aacute;c th&acirc;n thiện h&ograve;a c&ugrave;ng thi&ecirc;n nhi&ecirc;n v&agrave; đồng b&agrave;o c&aacute;c d&acirc;n tộc v&ugrave;ng cao.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/07/16/14/ivivu-ruong-bac-thang-hoang-su-phi.gif\" /></p>\r\n\r\n<p><em>Ho&agrave;ng Su Ph&igrave;.</em></p>\r\n\r\n<p>Buổi trưa: 11h45: Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng, trước khi tiếp tục cung đường di chuyển đi&nbsp;<strong>Việt Quang - Bắc Quang</strong>&nbsp;(thuộc địa phận&nbsp;<strong>H&agrave; Giang</strong>)</p>\r\n\r\n<p>Đo&agrave;n di chuyển&nbsp;tham quan ngắm&nbsp;ruộng bậc thang tại&nbsp;<strong>Bản Ph&ugrave;ng</strong>&nbsp;- được c&ocirc;ng nhận l&agrave; danh thắng di t&iacute;ch&nbsp;quốc gia (một trong năm địa danh kh&ocirc;ng thể bỏ qua khi đến&nbsp;<strong>Ho&agrave;ng Su Ph&igrave;</strong>&nbsp;săn cảnh đẹp m&ugrave;a l&uacute;a ch&iacute;n. Bản Ph&ugrave;ng một trong 6 bản (Bản Luốc, S&aacute;n Sả Hồ, Bản Ph&ugrave;ng, Hồ Thầu, Nậm Ty v&agrave; Th&ocirc;ng Nguy&ecirc;n) nổi tiếng với những ruộng bậc thang đẹp tạo n&ecirc;n từ thi&ecirc;n nhi&ecirc;n v&agrave; b&agrave;n tay kh&eacute;o l&eacute;o của con người từ h&agrave;ng trăm năm nay - nơi sinh sống chủ yếu của d&acirc;n tộc La Ch&iacute;, nổi tiếng với những ruộng l&uacute;a bậc thang tr&ugrave;ng điệp như h&ograve;a trộn giữa l&agrave;n m&acirc;y trắng v&ugrave;ng cao.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/07/16/15/ivivu-hoang-su-phi-1.gif\" /></p>\r\n\r\n<p><em>Ruộng Bậc Thang Bản Ph&ugrave;ng.</em></p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh, xe v&agrave; HDV sẽ đưa đo&agrave;n về th&ocirc;n&nbsp;<strong>Nậm Hồng (Th&ocirc;ng Nguy&ecirc;n)</strong>&nbsp;- điểm dừng ch&acirc;n ch&iacute;nh của h&agrave;nh tr&igrave;nh. Đo&agrave;n nhận ph&ograve;ng homestay tại th&ocirc;n<strong>&nbsp;Nậm Hồng</strong>.</p>\r\n\r\n<p>Buổi tối: 18h30:&nbsp;Qu&yacute; đo&agrave;n d&ugrave;ng bữa tối tại homestay c&ugrave;ng đồng b&agrave;o v&ugrave;ng cao.&nbsp;</p>\r\n\r\n<p>Sau bữa tối Qu&yacute; kh&aacute;ch được c&ugrave;ng d&acirc;n bản tự l&agrave;m cho m&igrave;nh những phần x&ocirc;i ngũ sắc - một m&oacute;n ăn truyền thống trong những ng&agrave;y mừng l&uacute;a mới, c&ugrave;ng thưởng thức văn nghệ ở bản hoặc tự do tham quan v&agrave; t&igrave;m hiểu phong tục đời sống của người d&acirc;n nơi đ&acirc;y.</p>\r\n\r\n<h3>NG&Agrave;Y 02: HO&Agrave;NG SU PH&Igrave; &ndash; L&Agrave;O CAI &ndash; Y T&Yacute; (ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>Buổi s&aacute;ng: Qu&yacute; đo&agrave;n thức dậy sớm, h&iacute;t căng lồng ngực kh&ocirc;ng kh&iacute; trong l&agrave;nh trong buổi ban mai với những l&agrave;n sương mỏng c&ograve;n vương tr&ecirc;n l&aacute; l&uacute;a ch&iacute;n v&agrave;ng.</p>\r\n\r\n<p>Xe v&agrave; HDV sẽ đưa Qu&yacute; kh&aacute;ch đi tham&nbsp;bản&nbsp;<strong>Nậm Hồng (Th&ocirc;ng Nguy&ecirc;n)</strong>&nbsp;- Địa danh nằm c&aacute;ch trung t&acirc;m huyện&nbsp;<strong>Ho&agrave;ng Su Ph&igrave;</strong>&nbsp;khoảng 40 km, th&ocirc;n Nậm Hồng, x&atilde; Th&ocirc;ng Nguy&ecirc;n l&agrave; nơi sinh sống của 37 hộ đồng b&agrave;o d&acirc;n tộc Dao đỏ. Nơi đ&acirc;y c&oacute; những thửa ruộng bậc thang trải d&agrave;i tr&ecirc;n c&aacute;c sườn đồi đ&atilde; được c&ocirc;ng nhận danh lam thắng cảnh cấp Quốc gia, b&ecirc;n cạnh đ&oacute; l&agrave; v&ugrave;ng ch&egrave; Shan tuyết cổ thụ Ph&igrave;n Hồ c&ugrave;ng với những n&eacute;t văn h&oacute;a đặc sắc của đồng b&agrave;o Dao đỏ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/01/15/ivivu-thong-nguyen.gif\" /></p>\r\n\r\n<p><em>Bản Nậm Hồng Th&ocirc;ng Nguy&ecirc;n.</em></p>\r\n\r\n<p>- Ruộng bậc thang tại&nbsp;<strong>Th&ocirc;ng Nguy&ecirc;n&nbsp;</strong>- nằm ở tả ngạn, nơi hội tụ d&ograve;ng chảy của 3 con suối lớn: Ph&igrave;n Hồ đổ xuống, Nậm &Ocirc;ng chảy về v&agrave; Nậm Kh&ograve;a dội sang c&ugrave;ng đổ v&agrave;o một chỗ tạo th&agrave;nh một b&igrave;nh nguy&ecirc;n ở ngay tr&ecirc;n lưng chừng n&uacute;i. Nhiều người nhận x&eacute;t rằng, Th&ocirc;ng Nguy&ecirc;n&nbsp; l&agrave; nơi &ldquo;Quần Sơn - Tụ Thủy&rdquo;.&nbsp;<strong>Th&ocirc;ng Nguy&ecirc;n</strong>&nbsp;l&agrave; một trong những địa điểm ngắm ruộng bậc thang tuyệt đẹp ở Ho&agrave;ng Su Ph&igrave;.</p>\r\n\r\n<p>-&nbsp;Cổng trời&nbsp;<strong>Ho&agrave;ng Su Ph&igrave;</strong>, l&agrave; cửa ng&otilde; để v&agrave;o 2 huyện n&uacute;i đất cao&nbsp;<strong>Ho&agrave;ng Su Ph&igrave;</strong>&nbsp;v&agrave;&nbsp;<strong>X&iacute;n Mần</strong>. Với những biển m&acirc;y tuyệt đẹp v&agrave; những th&aacute;c nước tuyệt đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/20/17/ivivu-hoang-su-phi-cong-troi.gif\" />&nbsp;<em>Ho&agrave;ng Su Ph&igrave; v&agrave; X&iacute;n Mần.</em></p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh, đo&agrave;n trả ph&ograve;ng di chuyển đi&nbsp;<strong>L&agrave;o Cai</strong>.</p>\r\n\r\n<p>Buổi trưa: Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng, thưởng thức ẩm thực địa phương tại nh&agrave; h&agrave;ng ở&nbsp;<strong>L&agrave;o Cai.</strong></p>\r\n\r\n<p>Buổi Chiều:&nbsp;Sau bữa trưa, đo&agrave;n tiếp tục h&agrave;nh tr&igrave;nh đi&nbsp;<strong>Y T&yacute;</strong>.</p>\r\n\r\n<p>Sau bữa trưa xe v&agrave; HDV đưa qu&yacute; kh&aacute;ch di chuyển về&nbsp;<strong>Y T&yacute;</strong>. Tr&ecirc;n đường đi Qu&yacute; kh&aacute;ch sẽ dừng ch&acirc;n thăm quan&nbsp;<strong>cột mốc 92</strong>&nbsp;- Ng&atilde; 3&nbsp;<strong>Lũng P&ocirc;</strong>&nbsp;- nơi con s&ocirc;ng Hồng chảy v&agrave;o đất Việt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/20/15/ivivu-cot-moc92.gif\" /></p>\r\n\r\n<p><em>Cột Mốc 92.</em></p>\r\n\r\n<p>17h30:&nbsp;Qu&yacute; kh&aacute;ch c&oacute; mặt tại homestay ở&nbsp;<strong>Y T&yacute;</strong>&nbsp;nhận ph&ograve;ng v&agrave; d&ugrave;ng bữa tối tại homestay với những m&oacute;n ăn d&acirc;n tộc độc đ&aacute;o của đồng b&agrave;o d&acirc;n tộc M&ocirc;ng nơi đ&acirc;y</p>\r\n\r\n<p>Buổi Tối:&nbsp;Ăn tối tại nh&agrave; h&agrave;ng. Tự do dạo chơi v&agrave; kh&aacute;m ph&aacute;&nbsp;<strong>Y T&yacute;</strong>&nbsp;về đ&ecirc;m.</p>\r\n\r\n<h3>NG&Agrave;Y 03: Y T&Yacute; SĂN M&Acirc;Y &ndash; SAPA &ndash; BẢN C&Aacute;T C&Aacute;T&nbsp; (ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p>Buổi s&aacute;ng:&nbsp;Đo&agrave;n d&ugrave;ng bữa s&aacute;ng tại nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>Sau bữa s&aacute;ng, qu&yacute; kh&aacute;ch trả ph&ograve;ng, xe v&agrave; hướng dẫn vi&ecirc;n đưa đo&agrave;n đi kh&aacute;m ph&aacute;:</p>\r\n\r\n<p>Đo&agrave;n di chuyển từ A L&ugrave; sang<strong>&nbsp;Ngải Thầu</strong>&nbsp;đ&acirc;y được coi l&agrave; một trong những đoạn đường đẹp nhất trong h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute;&nbsp;<strong>Y T&yacute;</strong>&nbsp;v&agrave;o m&ugrave;a l&uacute;a ch&iacute;n, cũng l&agrave; một trong những điểm rất đẹp v&agrave; th&iacute;ch hợp để c&oacute; thể săn m&acirc;y Y T&yacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/20/14/ivivu-san-may-y-ty1.gif\" /></p>\r\n\r\n<p><em>Săn M&acirc;y Y T&yacute;.</em></p>\r\n\r\n<p>Lưu &yacute; với bạn rằng: săn m&acirc;y cũng cần phải c&oacute; ch&uacute;t &iacute;t may mắn mới bắt gặp đ&uacute;ng những cảnh th&aacute;c đổ, m&acirc;y vần vũ...</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/20/14/ivivu-bien-may-y-ty.gif\" /></p>\r\n\r\n<p><em>Biển M&acirc;y Y T&yacute;.</em></p>\r\n\r\n<p><strong>Thung lũng Thề Pả (Y T&yacute;)</strong>&nbsp;- Thề Pả theo người địa phương nghĩa l&agrave; ruộng ch&acirc;n n&uacute;i hoặc ruộng đ&aacute;y. Ruộng bậc thang ở đ&acirc;y chủ yếu do người H&agrave; Nh&igrave; v&agrave; người M&ocirc;ng ở x&atilde; Y T&yacute; canh t&aacute;c với h&agrave;ng ngh&igrave;n thửa ruộng bậc thang m&ecirc;nh mang, uốn lượn, trải d&agrave;i h&agrave;ng km từ Choỏn Th&egrave;n đến cầu Thi&ecirc;n Sinh. Thắng cảnh n&agrave;y cũng được phong tặng l&agrave; di t&iacute;ch Quốc gia - một trong những cảnh điểm xứng đ&aacute;ng l&agrave; điểm đến của cuộc đời mỗi con người.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/20/14/ivivu-y-ty.gif\" /></p>\r\n\r\n<p><em>Ruộng Bậc Thang Y T&yacute;.</em></p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh, xe v&agrave; HDV sẽ đưa Qu&yacute; kh&aacute;ch di chuyển đi&nbsp;<strong>Choản Th&egrave;n</strong>&nbsp;- nơi c&oacute; những c&aacute;nh đồng l&uacute;a ch&iacute;n v&agrave;ng đẹp nhất được bao quanh bởi những ng&ocirc;i nh&agrave; tr&igrave;nh tường bằng đất - một kiểu nh&agrave; cư tr&uacute; quen thuộc của người M&ocirc;ng, Dao, H&agrave; Nh&igrave;&hellip;</p>\r\n\r\n<p>Buổi trưa: 11h30:&nbsp;Đo&agrave;n d&ugrave;ng bữa trưa tại&nbsp;nh&agrave; h&agrave;ng trước khi khởi h&agrave;nh di chuyển đi&nbsp;<strong>Sa Pa</strong>.</p>\r\n\r\n<p>Xe đưa đo&agrave;n l&ecirc;n khu du lịch&nbsp;<strong>Sapa</strong>&nbsp;- nơi m&agrave; người Ph&aacute;p xưa gọi l&agrave; &ldquo;kinh đ&ocirc; m&ugrave;a h&egrave; của xứ Bắc Kỳ&rdquo;.</p>\r\n\r\n<p>Buổi Chiều: Đến&nbsp;<strong>Sapa</strong>, Qu&yacute; kh&aacute;ch tham quan khu du lịch n&uacute;i&nbsp;<strong>H&agrave;m Rồng</strong>&nbsp;với: vườn L&ecirc;, vườn T&aacute;o M&egrave;o, cổng trời, vườn hoa Ch&acirc;u &Acirc;u, chinh phục đỉnh s&acirc;n M&acirc;y cao 1800m - ngắm to&agrave;n cảnh sapa từ tr&ecirc;n cao. Xem biểu diễn chương tr&igrave;nh ca m&uacute;a nhạc đặc sắc của v&ugrave;ng n&uacute;i T&acirc;y Bắc do c&aacute;c ch&agrave;ng trai c&ocirc; g&aacute;i người d&acirc;n tộc H&#39;m&ocirc;ng, Dao, T&agrave;y, H&agrave; Nh&igrave;... biểu diễn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/10/06/16/ivivu-tour-mien-bac-6n5d-sai-gon-moc-chau-son-la-sapa-ham-rong.jpg\" /></p>\r\n\r\n<p><em>H&agrave;m Rồng.</em></p>\r\n\r\n<p>Buổi tối: Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối ở nh&agrave; h&agrave;ng với đặc sản c&aacute; hồi Sa Pa. Qu&yacute; kh&aacute;ch đi dạo chơi TT.Sapa về đ&ecirc;m, tự do thưởng thức: Thắng Cố, cơm lam, lợn cắp n&aacute;ch, khoai nướng, bắp nướng&hellip;Nghỉ đ&ecirc;m tại thị trấn Sapa.</p>\r\n\r\n<h3>NG&Agrave;Y 04: SAPA &ndash; PHANXIPANG L&Agrave;O CAI &ndash; H&Agrave; NỘI TIỄN S&Acirc;N BAY (ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p>Buổi s&aacute;ng: Xe đưa qu&yacute; kh&aacute;ch Xe đưa qu&yacute; kh&aacute;ch đi tham quan, Chinh phục đỉnh&nbsp;<strong>Fansipan</strong>&nbsp;bằng hệ thống c&aacute;p treo lớn (Chi ph&iacute; c&aacute;p treo tự t&uacute;c) v&agrave; hiện đại nhất Thế Giới hiện n&agrave;y (1 cabin c&oacute; thể ngồi từ 34-35 người) Tới đỉnh<strong>&nbsp;Fansipan</strong>&nbsp;qu&yacute; kh&aacute;ch thưởng thức cảm gi&aacute;c đứng tr&ecirc;n m&acirc;y, ngắm nh&igrave;n thị trấn Sapa, sau đ&oacute; tiếp tục leo 639 bậc để tận mắt thấy cột mộc Fansipan độ cao 3.143m nơi được coi l&agrave; &ldquo;n&oacute;c nh&agrave; Đ&ocirc;ng Dương&rdquo;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/20/15/ivivu-fansipan.gif\" /></p>\r\n\r\n<p><em>Fansipang.</em></p>\r\n\r\n<p>Đi tham quan&nbsp;<strong>Bản C&aacute;t C&aacute;t</strong>&nbsp;- địa b&agrave;n cư tr&uacute; của người H&#39;M&ocirc;ng, ngắm cảnh h&ugrave;ng vĩ của n&uacute;i rừng T&acirc;y Bắc, thăm quan Th&aacute;c Thuỷ Điện do Ph&aacute;p x&acirc;y dựng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/15/17/ivivu-ban-cat-cat-2.gif\" /></p>\r\n\r\n<p><em>Bản C&aacute;t C&aacute;t.</em></p>\r\n\r\n<p>Đến nh&agrave; h&agrave;ng ăn trưa.</p>\r\n\r\n<p>Buổi chiều: Thăm quan&nbsp;<strong>TP. L&agrave;o Cai</strong>, cửa khẩu L&agrave;o Cai, thăm quan chợ v&ugrave;ng bi&ecirc;n&nbsp;<strong>Cốc Lếu</strong>. Khởi h&agrave;nh về H&agrave; Nội theo tuyến đường cao tốc d&agrave;i nhất Việt Nam L&agrave;o Cai - H&agrave; Nội 250km.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/19/11/ivivu-cho-coc-leu-lao-cai.gif\" /></p>\r\n\r\n<p><em>Chợ Cốc Lếu.</em></p>\r\n', 1, 1),
(23, 'Tour Đà Lạt 1N1D', '33323', '3200000', 11, 'dll.jpeg', '<h3>Trải Nghiệm C&acirc;y Th&ocirc;ng C&ocirc; Đơn Thật &#39;Chill&#39;</h3>\r\n\r\n<p>Mặc d&ugrave; đường đi đến đ&acirc;y rất gian nan v&agrave; vất vả, nhưng rất nhiều người, đặc biệt c&aacute;c bạn trẻ vẫn t&igrave;m đến. Sau khi chinh phục con đường đ&egrave;o dốc trơn trượt, hiện ra trước mặt khung cảnh l&atilde;ng mạn &ldquo;c&acirc;y th&ocirc;ng c&ocirc; đơn&rdquo; đứng sừng sững, đơn độc giữa một khung cảnh n&ecirc;n thơ, một b&ecirc;n l&agrave; hồ nước trong veo, một b&ecirc;n l&agrave; ngọn n&uacute;i Lang Biang huyền thoại bạt ng&agrave;n th&ocirc;ng xanh.Từ đ&oacute;, &ldquo;c&acirc;y th&ocirc;ng c&ocirc; đơn&rdquo; b&ecirc;n hồ Đan Kia &ndash; Suối V&agrave;ng trở th&agrave;nh một trong những h&igrave;nh ảnh biểu tượng của xứ sở sường m&ugrave;, l&agrave; nơi c&aacute;c cặp t&igrave;nh nh&acirc;n thường check &ndash; in, t&igrave;m đến để &ldquo;sống ảo&rdquo;&hellip; C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n', '<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>H&agrave;nh tr&igrave;nh đến với c&acirc;y th&ocirc;ng c&ocirc; đơn kh&ocirc;ng chỉ để check in &#39;C&acirc;y Th&ocirc;ng C&ocirc; Đơn&#39; m&agrave; c&ograve;n:</p>\r\n\r\n<p>- Cắm trại thưởng thức tiệc nướng</p>\r\n\r\n<p>- Săn m&acirc;y, ngắm b&igrave;nh minh tại hồ nước b&ecirc;n C&acirc;y Th&ocirc;ng&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>CHIỀU NG&Agrave;Y 01: H&Agrave;NH TR&Igrave;NH KH&Aacute;M PH&Aacute; &quot;C&Acirc;Y TH&Ocirc;NG C&Ocirc; ĐƠN&quot; (ĂN TỐI)&nbsp;</h3>\r\n\r\n<p>15:00: Qu&yacute; kh&aacute;ch tập trung tại Kh&aacute;ch sạn&nbsp;Dala Hotel&nbsp;địa chỉ số&nbsp;158 B&ugrave;i thị Xu&acirc;n, Phường 2, TP Đ&agrave; Lạt&nbsp;hoặc xe của&nbsp;c&ocirc;ng ty&nbsp;sẽ đón quý khách trực ti&ecirc;́p tại đi&ecirc;̉m hẹn.&nbsp;</p>\r\n\r\n<p>C&aacute;c cung đường đ&oacute;n kh&aacute;ch:&nbsp;B&ugrave;i Thị Xu&acirc;n, Phan Bội Ch&acirc;u, Ph&ugrave; Đổng Thi&ecirc;n Vương,&nbsp;Phan Đ&igrave;nh Ph&ugrave;ng, Hai B&agrave; Trưng, Nam Kỳ Khởi Nghĩa, Trần Ph&uacute;, khu vực chợ Đ&agrave; Lạt. Sau đó di chuy&ecirc;̉n đ&ecirc;́n nơi cắm trại.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/15/ivivu-trekking-cay-thong.gif\" /></p>\r\n\r\n<p><em>Trekking Đến Khu Cắm Trại.</em></p>\r\n\r\n<p>16:00: Tham gia chương tr&igrave;nh cắm trại, C&ugrave;ng nhau trải nghiệm dựng lều trại.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-dung-trai.gif\" /></p>\r\n\r\n<p><em>Cả Đo&agrave;n Dựng Trại.</em></p>\r\n\r\n<p>Kiếm những c&agrave;nh củi kh&ocirc; v&agrave; chuẩn bị thịt nướng cho bữa tối. Đồ ăn nhẹ cho bữa chiều: B&aacute;nh m&igrave;, bơ, đường.</p>\r\n\r\n<p>18:30: Mọi người c&ugrave;ng nhau thưởng thức bữa tối c&ugrave;ng với nhau: Đồ ăn BBQ (Thịt g&agrave; nướng mọi, Thịt heo sả ớt, T&ocirc;m sa tế, Rau salad trộn dầu dấm, Tr&aacute;i c&acirc;y tr&aacute;ng miệng).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-bbq.gif\" /></p>\r\n\r\n<p><em>BBQ Tiệc Nướng.</em></p>\r\n\r\n<p>Thưởng thức rượu Cần hoặc rượu Vang Dalat trong thời tiết se se lạnh của n&uacute;i rừng T&acirc;y Nguy&ecirc;n.</p>\r\n\r\n<p>20:00: C&ugrave;ng nhau tham gia đốt lửa trại c&ugrave;ng nhau nướng khoai v&agrave; bắp tr&ograve; truyện b&ecirc;n bếp lữa.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-bua-toi-bbq.gif\" /></p>\r\n\r\n<p><em>Tr&ograve; Chuyện B&ecirc;n Bếp Lửa.</em></p>\r\n\r\n<p>Buổi tối mọi người c&oacute; thể c&ugrave;ng nhau t&acirc;m sự, h&aacute;t h&ograve; v&agrave; c&ugrave;ng nhau ngồi ngắm sao rơi tại đồi cỏ thật tuyệt kh&ocirc;ng c&ograve;n g&igrave; bằng.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-trai-dem.gif\" /></p>\r\n\r\n<p><em>Trại Ban Đ&ecirc;m.</em></p>\r\n\r\n<p>22:00: Mọi người đi ngủ sớm để buổi s&aacute;ng thức dậy ở một nơi xa.</p>\r\n\r\n<h3>NG&Agrave;Y 02: Đ&Oacute;N CH&Agrave;O NG&Agrave;Y MỚI (ĂN S&Aacute;NG)&nbsp;</h3>\r\n\r\n<p>5:00: C&ugrave;ng nhau thức dậy, vệ sinh c&aacute; nh&acirc;n v&agrave; đ&oacute;n b&igrave;nh minh nơi phố vắng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-binh-minh-cay-thong.gif\" /></p>\r\n\r\n<p><em>B&igrave;nh Minh Buổi S&aacute;ng.</em></p>\r\n\r\n<p>Săn m&acirc;y lưu dữ những bức h&igrave;nh đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/15/ivivu-sang-som-cay-thong.gif\" /></p>\r\n\r\n<p><em>Săn M&acirc;y S&aacute;ng Sớm.</em></p>\r\n', 1, 1),
(24, 'Săn Mây Đón Bình Minh', '216567', '1000000', 10, 'dll1.jpeg', '<h3>Bắt trọn khoảng khắc vẻ đẹp thi&ecirc;n nhi&ecirc;n cảnh sắc Đ&agrave; Lạt</h3>\r\n\r\n<p>Dậy sớm săn m&acirc;y ở Đ&agrave; Lạt l&agrave; một trong những trải nghiệm đ&aacute;ng nhớ khi lỡ gh&eacute; thăm nơi đ&acirc;y. V&agrave;o m&ugrave;a n&agrave;y, những đợt m&acirc;y m&ugrave; giăng lối khắp c&aacute;c đồi n&uacute;i sẽ l&agrave;m say l&ograve;ng bất cứ ai bởi vẻ đẹp tựa như tranh vẽ.&nbsp;Đồng thời, đ&oacute;n b&igrave;nh m&igrave;nh buổi s&aacute;ng ở 1 nơi c&ograve;n hoang sơ, lưu giữ vẻ đẹp của thi&ecirc;n nhi&ecirc;n như vậy cũng gi&uacute;p bạn lấy tinh thần cho 1 ng&agrave;y mới tr&agrave;n đầy năng lượng. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n', '<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Ngắm b&igrave;nh minh ở Đ&agrave; Lạt c&ugrave;ng người thương chắc chắn sẽ l&agrave; một trong những trải nghiệm tuyệt vời nhất qua c&aacute;c điểm:</p>\r\n\r\n<p>- Đ&oacute;n b&igrave;nh minh, săn m&acirc;y tại khu vực đồi ch&egrave;&nbsp;<strong>Cầu Đất</strong>.</p>\r\n\r\n<p>- C&aacute;nh đồng hoa&nbsp;<strong>Cẩm T&uacute; Cầu</strong>.</p>\r\n\r\n<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<p>Khởi h&agrave;nh h&agrave;ng ng&agrave;y: từ 4h00 đến 11h00</p>\r\n\r\n<p>04h00: Hướng dẫn vi&ecirc;n của c&ocirc;ng ty sẽ đ&oacute;n qu&yacute; kh&aacute;ch đến điểm săn m&acirc;y.</p>\r\n\r\n<p>- Đ&oacute;n b&igrave;nh minh tr&ecirc;n thảm gỗ săn m&acirc;y ảo diệu (di chuyển bằng &quot;si&ecirc;u xe Jeep&quot; cực ngầu)</p>\r\n\r\n<p>Địa chỉ: Trong khu vực đồi ch&egrave;&nbsp;<strong>Cầu Đất</strong>, th&ocirc;n&nbsp;<strong>Trường Thọ</strong>, x&atilde;&nbsp;<strong>Trạm H&agrave;nh</strong>,&nbsp;<strong>Đ&agrave; Lạt</strong>.</p>\r\n\r\n<p>B&igrave;nh minh l&agrave; khoảnh khắc ng&agrave;y mới bắt đầu khi mặt trời dần l&oacute; rạng l&ecirc;n cao dần, sương m&ugrave; giăng lối dần gi&atilde;n ra nhường chỗ cho những tia nắng ấm &aacute;p đầu ti&ecirc;n. Th&agrave;nh phố&nbsp;<strong>Đ&agrave; Lạt</strong>&nbsp;mang địa h&igrave;nh đặc biệt ở tr&ecirc;n cao, vậy n&ecirc;n c&oacute; nhiều địa điểm săn m&acirc;y cực kỳ vi diệu, hơn nữa trong c&aacute;i kh&iacute; hậu se lạnh th&igrave; s&aacute;ng sớm hay c&oacute; sương m&ugrave; d&agrave;y đặc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/29/13/ivivu-san-may-dl-2.gif\" /></p>\r\n\r\n<p><em>Săn M&acirc;y Đ&agrave; Lạt.</em></p>\r\n\r\n<p>- Tham quan v&agrave; thưởng thức &quot;cơ sở sản xuất&nbsp;<strong>vườn hồng Treo</strong>&quot;: Tới nơi, du kh&aacute;ch được tận mắt chứng kiến quy tr&igrave;nh sản xuất hồng treo, bao nhi&ecirc;u l&agrave; quả hồng phơi theo d&acirc;y dọc xuống khi trời nắng c&oacute; gi&oacute;. V&agrave;o đ&acirc;y bạn thoải m&aacute;i chụp h&igrave;nh c&ugrave;ng, c&oacute; điều cẩn thận kẻo l&agrave;m rơi, l&agrave;m bẩn hay hỏng hồng nh&eacute;. Chủ cơ sở sẽ giới thiệu về thức qu&agrave; n&agrave;y v&agrave; qu&aacute; tr&igrave;nh l&agrave;m hồng vất vả ra sao.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/29/14/ivivu-vuon-hong-treo.gif\" /></p>\r\n\r\n<p><em>Vườn Hồng Treo.</em></p>\r\n\r\n<p>Hồng treo đạt chuẩn l&agrave; c&oacute; m&agrave;u v&agrave;ng đỏ, b&ecirc;n ngo&agrave;i cứng, trong th&igrave; mềm c&oacute; mật ngon, thơm, bổ dưỡng. Cắn miếng hồng, nhấp ngụm tr&agrave; n&oacute;ng đ&uacute;ng hợp vị nhau lu&ocirc;n.&nbsp;</p>\r\n\r\n<p>- Check in đồi ch&egrave;&nbsp;<strong>Cầu Đất Farm</strong>&nbsp;(Ăn sáng tự túc)</p>\r\n\r\n<p>Kh&ocirc;ng gian rộng lớn, đứng tr&ecirc;n đồi cao ngắm m&agrave;u xanh t&iacute;t tắp tới tận ch&acirc;n trời, hướng mắt ra xa thấy những m&aacute;i nh&agrave; tranh của người d&acirc;n thấp tho&aacute;ng. Người n&ocirc;ng d&acirc;n chăm chỉ h&aacute;i l&aacute; trước khi trời tối, cảnh tượng y&ecirc;n b&igrave;nh đến lạ. Tới đ&acirc;y bạn được tham quan quy tr&igrave;nh sản xuất, chế biến ch&egrave; c&aacute;c loại v&agrave; được giới thiệu về ch&egrave; ngon, thử thưởng thức v&agrave; mua về l&agrave;m qu&agrave; tặng.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/03/06/15/ivivu-tour-da-lat-doi-che-cau-dat-farm.jpg\" /></p>\r\n\r\n<p><em>Đồi Ch&egrave; Cầu Đất.</em></p>\r\n\r\n<p>-&nbsp;<strong>Vườn d&acirc;u T&acirc;y c&ocirc;ng nghệ cao</strong>: Đến với Đ&agrave; Lạt m&agrave; kh&ocirc;ng tham quan vườn d&acirc;u quả l&agrave; l&atilde;ng ph&iacute;. Ch&uacute;ng ta thường biết th&agrave;nh phố n&agrave;y c&oacute; kh&iacute; hậu &ocirc;n h&ograve;a, đất đai ph&igrave; nhi&ecirc;u trồng giống d&acirc;u t&acirc;y vừa to quả, lại ch&iacute;n đỏ mọng ngọt thơm. Người ta trồng d&acirc;u th&agrave;nh cả vườn, trang trại lớn theo c&ocirc;ng nghệ cao, ứng dụng kỹ thuật hiện đại trong trồng v&agrave; chăm s&oacute;c. D&acirc;u c&oacute; thể trồng ở ngo&agrave;i trời hoặc trong nh&agrave; k&iacute;nh, l&agrave;m gi&agrave;n treo c&oacute; hệ thống tưới ti&ecirc;u.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/11/17/ivivu-vuon-dau-dl.gif\" /></p>\r\n\r\n<p><em>Vườn D&acirc;u T&acirc;y C&ocirc;ng Nghệ.</em></p>\r\n\r\n<p>-&nbsp;<strong>Nắng Coffee</strong>&nbsp;&ndash; Tọa độ sống ảo cực xịn tại&nbsp;<strong>Trại M&aacute;t</strong>.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2021/03/29/09/ivivu-nang-coffer.gif\" /></p>\r\n\r\n<p><em>Nắng Coffee.</em></p>\r\n', 1, 6),
(25, 'Cắm Trại Cây Thông Cô Đơn', '432423', '700000', 11, 'dll.jpeg', '<h3>Trải Nghiệm C&acirc;y Th&ocirc;ng C&ocirc; Đơn Thật &#39;Chill&#39;</h3>\r\n', '<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>CHIỀU NG&Agrave;Y 01: H&Agrave;NH TR&Igrave;NH KH&Aacute;M PH&Aacute; &quot;C&Acirc;Y TH&Ocirc;NG C&Ocirc; ĐƠN&quot; (ĂN TỐI)&nbsp;</h3>\r\n\r\n<p>15:00: Qu&yacute; kh&aacute;ch tập trung tại Kh&aacute;ch sạn&nbsp;Dala Hotel&nbsp;địa chỉ số&nbsp;158 B&ugrave;i thị Xu&acirc;n, Phường 2, TP Đ&agrave; Lạt&nbsp;hoặc xe của&nbsp;c&ocirc;ng ty&nbsp;sẽ đón quý khách trực ti&ecirc;́p tại đi&ecirc;̉m hẹn.&nbsp;</p>\r\n\r\n<p>C&aacute;c cung đường đ&oacute;n kh&aacute;ch:&nbsp;B&ugrave;i Thị Xu&acirc;n, Phan Bội Ch&acirc;u, Ph&ugrave; Đổng Thi&ecirc;n Vương,&nbsp;Phan Đ&igrave;nh Ph&ugrave;ng, Hai B&agrave; Trưng, Nam Kỳ Khởi Nghĩa, Trần Ph&uacute;, khu vực chợ Đ&agrave; Lạt. Sau đó di chuy&ecirc;̉n đ&ecirc;́n nơi cắm trại.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/15/ivivu-trekking-cay-thong.gif\" /></p>\r\n\r\n<p><em>Trekking Đến Khu Cắm Trại.</em></p>\r\n\r\n<p>16:00: Tham gia chương tr&igrave;nh cắm trại, C&ugrave;ng nhau trải nghiệm dựng lều trại.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-dung-trai.gif\" /></p>\r\n\r\n<p><em>Cả Đo&agrave;n Dựng Trại.</em></p>\r\n\r\n<p>Kiếm những c&agrave;nh củi kh&ocirc; v&agrave; chuẩn bị thịt nướng cho bữa tối. Đồ ăn nhẹ cho bữa chiều: B&aacute;nh m&igrave;, bơ, đường.</p>\r\n\r\n<p>18:30: Mọi người c&ugrave;ng nhau thưởng thức bữa tối c&ugrave;ng với nhau: Đồ ăn BBQ (Thịt g&agrave; nướng mọi, Thịt heo sả ớt, T&ocirc;m sa tế, Rau salad trộn dầu dấm, Tr&aacute;i c&acirc;y tr&aacute;ng miệng).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-bbq.gif\" /></p>\r\n\r\n<p><em>BBQ Tiệc Nướng.</em></p>\r\n\r\n<p>Thưởng thức rượu Cần hoặc rượu Vang Dalat trong thời tiết se se lạnh của n&uacute;i rừng T&acirc;y Nguy&ecirc;n.</p>\r\n\r\n<p>20:00: C&ugrave;ng nhau tham gia đốt lửa trại c&ugrave;ng nhau nướng khoai v&agrave; bắp tr&ograve; truyện b&ecirc;n bếp lữa.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-bua-toi-bbq.gif\" /></p>\r\n\r\n<p><em>Tr&ograve; Chuyện B&ecirc;n Bếp Lửa.</em></p>\r\n\r\n<p>Buổi tối mọi người c&oacute; thể c&ugrave;ng nhau t&acirc;m sự, h&aacute;t h&ograve; v&agrave; c&ugrave;ng nhau ngồi ngắm sao rơi tại đồi cỏ thật tuyệt kh&ocirc;ng c&ograve;n g&igrave; bằng.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-trai-dem.gif\" /></p>\r\n\r\n<p><em>Trại Ban Đ&ecirc;m.</em></p>\r\n\r\n<p>22:00: Mọi người đi ngủ sớm để buổi s&aacute;ng thức dậy ở một nơi xa.</p>\r\n\r\n<h3>NG&Agrave;Y 02: Đ&Oacute;N CH&Agrave;O NG&Agrave;Y MỚI (ĂN S&Aacute;NG)&nbsp;</h3>\r\n\r\n<p>5:00: C&ugrave;ng nhau thức dậy, vệ sinh c&aacute; nh&acirc;n v&agrave; đ&oacute;n b&igrave;nh minh nơi phố vắng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/14/ivivu-binh-minh-cay-thong.gif\" /></p>\r\n\r\n<p><em>B&igrave;nh Minh Buổi S&aacute;ng.</em></p>\r\n\r\n<p>Săn m&acirc;y lưu dữ những bức h&igrave;nh đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/10/16/15/ivivu-sang-som-cay-thong.gif\" /></p>\r\n', 1, 6),
(26, 'Combo TripU 3N2Đ Đà Lạt Wonder Resort', '213324', '4000000', 11, 'dll2.jpeg', '<ul>\r\n	<li>02 đ&ecirc;m nghỉ tại ph&ograve;ng hướng vườn của Dalat Wonder Resort.</li>\r\n	<li>Buffet s&aacute;ng h&agrave;ng ng&agrave;y</li>\r\n	<li>Thưởng thức 01 bữa ăn (trưa hoặc tối) tại nh&agrave; h&agrave;ng Le Fonte l&atilde;ng mạn.</li>\r\n	<li>Miễn ph&iacute; tham quan c&ocirc;ng vi&ecirc;n giải tr&iacute; Wonderland.</li>\r\n	<li>Đưa đ&oacute;n miễn ph&iacute; giữa resort v&agrave; trung t&acirc;m th&agrave;nh phố Đ&agrave; Lạt theo lịch tr&igrave;nh của resort.</li>\r\n	<li>Tham quan s&acirc;n golf mini, hồ c&aacute; koi, suối hồng hạc, vườn d&acirc;u, vườn rau, vườn hoa theo m&ugrave;a tại khu nghỉ</li>\r\n	<li>Bể bơi v&ocirc; cực v&agrave; c&aacute;c tiện &iacute;ch, dịch vụ kh&aacute;c.</li>\r\n	<li>Thuế v&agrave; ph&iacute; phục vụ</li>\r\n</ul>\r\n', '', 1, 6),
(27, 'Tour Cao Cấp Phan Thiết 2N1D', '213321', '4000000', 10, 'pt.jpeg', '<h3>Phan Thiết - V&ugrave;ng Đất Đầy Nắng Gi&oacute;</h3>\r\n\r\n<p>Du lịch Phan Thiết lu&ocirc;n l&agrave; sự lựa chọn đối với nhiều du kh&aacute;ch trong v&agrave; ngo&agrave;i nước bởi th&agrave;nh phố Phan Thiết với &#39;biển xanh - c&aacute;t trắng - nắng v&agrave;ng&#39; v&agrave; điểm du lịch Mũi N&eacute; Phan Thiết gần Th&agrave;nh phố Hồ Ch&iacute; Minh n&ecirc;n rất được du kh&aacute;ch y&ecirc;u th&iacute;ch lựa chọn l&agrave; điểm nghỉ dưỡng, vui chơi v&agrave;o mỗi dịp nghỉ lễ hay cuối tuần. Đến với tour Phan Thiết, du kh&aacute;ch sẽ c&oacute; dịp chi&ecirc;u ngưỡng n&eacute;t đẹp hoang sơ từ phong cảnh đến những b&atilde;i biển chưa được kh&aacute;m ph&aacute;, được thưởng thức những m&oacute;n ăn đậm chất miền biển. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n', '<h3>NG&Agrave;Y 01: TP. HCM &ndash; PHAN THIẾT (ĂN S&Aacute;NG, TRƯA, TỐI)&nbsp;</h3>\r\n\r\n<p>S&aacute;ng: 5h00 Xe v&agrave; HDV du lịch đ&oacute;n Qu&yacute; kh&aacute;ch tại điểm hẹn khởi h&agrave;nh đi<strong>&nbsp;Phan Thiết.</strong></p>\r\n\r\n<p>06h30: Đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại khu vực ng&atilde; ba dầu gi&acirc;y, nghỉ ngơi.</p>\r\n\r\n<p>07h30: Tiếp tục lộ tr&igrave;nh, đo&agrave;n tham gia c&aacute;c tr&ograve; chơi vui nhộn, h&aacute;t cho nhau nghe, thi h&aacute;t karaoke&hellip; Nghe thuyết minh về lịch sử v&agrave; giai thoại v&ugrave;ng đất m&agrave; đo&agrave;n đi qua.</p>\r\n\r\n<p>Trưa: 11h00 xe đưa đo&agrave;n v&agrave;o khu mũi n&eacute;, tr&ecirc;n đường ngoạn cảnh&nbsp;ngắm nh&igrave;n&nbsp;quần thể&nbsp;<strong>Th&aacute;p Poshanư</strong>&nbsp;&ndash; di t&iacute;ch kiến tr&uacute;c độc đ&aacute;o của d&acirc;n tộc&nbsp;<strong>ChămPa, Lầu &Ocirc;ng Ho&agrave;ng, b&atilde;i đ&aacute; &Ocirc;ng Địa</strong>&nbsp;v&agrave; rặng dừa&nbsp;<strong>H&agrave;m Tiến</strong>, l&agrave;ng ch&agrave;i Mũi N&eacute;.</p>\r\n\r\n<p>Đo&agrave;n d&ugrave;ng cơm trưa v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi, tắm biển v&agrave; tự do thưởng thức đặc sản.&nbsp;</p>\r\n\r\n<p>Menu trưa:&nbsp;</p>\r\n\r\n<p>1.&nbsp;Mực tươi nướng mọi</p>\r\n\r\n<p>2. Ch&aacute;o h&agrave;o</p>\r\n\r\n<p>3.&nbsp;Gỏi c&aacute; biển</p>\r\n\r\n<p>4. D&ograve;m hấp xả</p>\r\n\r\n<p>5. C&aacute; hấp + b&aacute;nh tr&aacute;ng</p>\r\n\r\n<p>6. S&ograve; điệp nướng mở h&agrave;nh&nbsp;</p>\r\n\r\n<p>7. Lẩu ghẹ + b&uacute;n tươi + rau</p>\r\n\r\n<p>8. Khăn lạnh + Tr&aacute;ng miệng.</p>\r\n\r\n<p>Chiều: 15h00 tham quan<strong>&nbsp;Suối Ti&ecirc;n -&nbsp;</strong>d&ograve;ng nước chảy nhẹ nh&agrave;ng men theo v&aacute;ch n&uacute;i. Nước ở Suối ti&ecirc;n chỉ đạt đến mắt c&aacute; ch&acirc;n n&ecirc;n bạn c&oacute; thể tha hồ lội suối dưới l&agrave;n nước m&aacute;t lạnh. C&agrave;ng đi bộ dọc theo con suối, một khung cảnh tuyệt vời như chốn bồng lai ti&ecirc;n cảnh c&agrave;ng mở ra trước mắt bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/12/09/11/ivivu-suoi-tien-2.gif\" /></p>\r\n\r\n<p><em>Suối Ti&ecirc;n.</em></p>\r\n\r\n<p>16h00: Đo&agrave;n tham quan&nbsp;<strong>Th&aacute;p Ch&agrave;m Poshanư</strong>&nbsp;l&agrave; một nh&oacute;m di t&iacute;ch c&ograve;n s&oacute;t lại của Vương quốc Chăm Pa xưa được x&acirc;y dựng v&agrave;o khoảng cuối thế kỷ thứ 8, đầu thế kỷ thứ 9 thờ thần Shiva-&nbsp; một trong những vị thần Ấn Độ gi&aacute;o được s&ugrave;ng b&aacute;i v&agrave; t&ocirc;n k&iacute;nh nhất trong văn h&oacute;a Champa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/12/09/11/ivivu-thap-posahnu.gif\" /></p>\r\n\r\n<p><em>Th&aacute;p Ch&agrave;m Poshanư.</em></p>\r\n\r\n<p>Tối:&nbsp;18h00&nbsp;Đo&agrave;n d&ugrave;ng bữa Hải Sản Tại Nh&agrave; H&agrave;ng C&acirc;y B&agrave;ng nổi tiếng ở&nbsp;<strong>Mũi N&eacute;.</strong></p>\r\n\r\n<p>Menu Tối:&nbsp;</p>\r\n\r\n<p>1. Mực một nắng nướng</p>\r\n\r\n<p>2. Gỏi c&aacute; C&acirc;y B&agrave;ng</p>\r\n\r\n<p>3. Ghẹ hấp (1/2con/kh&aacute;ch)</p>\r\n\r\n<p>4. Ốc hương hấp sả</p>\r\n\r\n<p>5. Ch&aacute;o h&agrave;o</p>\r\n\r\n<p>6. Cơm chi&ecirc;n c&aacute; mặn</p>\r\n\r\n<p>7. Lẩu hải sản + b&uacute;n tươi</p>\r\n\r\n<p>8. Khăn lạnh +nước kho&aacute;ng +tr&aacute;ng miệng</p>\r\n\r\n<p>20h00: Đo&agrave;n xem biễu diễn Show &quot;<strong>Fishermen Show&quot;</strong>&nbsp;&ndash; Huyền Thoại L&agrave;ng Ch&agrave;i, do vũ đo&agrave;n kịch đương đại biễu diễn tr&ecirc;n s&acirc;n khấu nhạc nước đầu ti&ecirc;n v&agrave; duy nhất tại Việt Nam. (Fishermen Show - Show Huyền Thoại L&agrave;ng Ch&agrave;i tr&ecirc;n s&acirc;n khấu nhạc nước biểu diễn h&agrave;ng đ&ecirc;m từ thứ 4, thứ 6, thứ 7 v&agrave; 10h30 s&aacute;ng chủ nhật) (Chi ph&iacute; tự t&uacute;c).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/12/09/11/ivivu-nhac-nuoc-fishermen-show.gif\" /></p>\r\n\r\n<p><em>Fishermen Show.</em></p>\r\n\r\n<p>21h00: Kết th&uacute;c chương tr&igrave;nh đo&agrave;n sinh hoạt tự do về đ&ecirc;m.</p>\r\n\r\n<h3>NG&Agrave;Y 02: B&Agrave;U SEN - PHAN THIẾT &ndash; TP.HCM (ĂN S&Aacute;NG, TRƯA)&nbsp;</h3>\r\n\r\n<p>Qu&yacute; kh&aacute;ch lựa chọn 1 trong 2 chương tr&igrave;nh như sau:&nbsp;</p>\r\n\r\n<p><em>Chương tr&igrave;nh 1</em>: S&aacute;ng: 05h30 xe Jeep v&agrave; HDV đưa qu&yacute; kh&aacute;ch đi tham quan&nbsp;<strong>Đồi C&aacute;t bay</strong>, một tuyệt t&aacute;c của thi&ecirc;n nhi&ecirc;n, nơi khơi nguồn s&aacute;ng tạo cho những t&aacute;c phẩm nghệ thuật nổi tiếng. Đến đ&acirc;y, đo&agrave;n c&oacute; dịp ngắm đồi c&aacute;t tr&ugrave;ng điệp với h&igrave;nh ảnh thật kỳ th&uacute;, c&ugrave;ng trượt c&aacute;t thật th&uacute; vị v&agrave; thưởng thức dừa địa phương m&aacute;t lạnh ngay dưới ch&acirc;n đồi c&aacute;t (Chi ph&iacute; thu&ecirc; thảm trượt v&agrave; sử dụng dịch vụ qu&yacute; kh&aacute;ch tự t&uacute;c).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/25/17/ivivu-tour-phan-thiet-2n1d-phan-thiet-ta-cu-doi-cat-bay.jpg\" /></p>\r\n\r\n<p><em>Đồi C&aacute;t Bay.</em></p>\r\n\r\n<p>07h30: Đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn, tự do tắm biển hoặc tham quan:</p>\r\n\r\n<p><strong>- L&acirc;u đài Vang RD</strong>&nbsp;trong khu nghỉ dưỡng&nbsp;<strong>Sealink.&nbsp;</strong>Tại đ&acirc;y, du kh&aacute;ch c&oacute; cảm gi&aacute;c trở về thời trung cổ của ch&acirc;u &Acirc;u với khu quảng trường rộng lớn, m&aacute;i v&ograve;m cổ k&iacute;nh, trang nh&atilde;, sang trọng, v&agrave; những th&aacute;p canh cao v&uacute;t trải tầm nh&igrave;n ra kh&ocirc;ng gian tươi xanh của Sea Links City và toàn vịnh Mũi Né. Ngo&agrave;i ra, Qu&yacute; kh&aacute;ch được nghe giới thiệu về quy tr&igrave;nh sản xuất, đ&oacute;ng chai v&agrave; thưởng thức một trong những loại rượu vang hảo hạng nổi tiếng thế giới được xuất xứ từ Thung lũng Napa - Mỹ (Chi ph&iacute; tự t&uacute;c).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/25/17/ivivu-tour-phan-thiet-2n1d-hai-dang-ke-ga-lau-dai-ruou-vang-lau-dai-ruou-vang.jpg\" /></p>\r\n\r\n<p><em>L&acirc;u Đ&agrave;i Rượu Vang.</em></p>\r\n\r\n<p><em>Chương tr&igrave;nh 02</em>: S&aacute;ng 06h30 Đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng&nbsp;</p>\r\n\r\n<p>07h30: Xe Jeep Đ&oacute;n đo&agrave;n tại Resort đưa đo&agrave;n đi tham quan&nbsp;<strong>B&agrave;u Sen</strong>&nbsp;(B&agrave;u Trắng) Đo&agrave;n ngắm cảnh những đồi c&aacute;t trắng tuyệt đẹp v&agrave; chinh phục đồi c&aacute;t với những chiếc xe moto địa h&igrave;nh hoặc kh&aacute;m ph&aacute; B&agrave;u Sen bằng những chiếc thuyền ch&egrave;o Kayat (Chi ph&iacute; vui chơi tự t&uacute;c).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/04/09/10/ivivu-bau-sen.jpg\" /></p>\r\n\r\n<p><em>B&agrave;u Sen.</em></p>\r\n', 1, 5),
(28, 'Tour Thám Hiểm Phong Nha', '123141', '4000000', 20, 'dong.jpeg', '<h3>H&agrave;nh Tr&igrave;nh Trải Nghiệm Hang Ti&ecirc;n</h3>\r\n\r\n<p>Hang Ti&ecirc;n &ndash; được đặt t&ecirc;n theo truyền thuyết những vị ti&ecirc;n nữ gi&aacute;ng trần mải thưởng ngoạn m&agrave; qu&ecirc;n đường về trời - l&agrave; một hang động h&ugrave;ng vĩ nhưng chỉ được giới thiệu v&agrave; tham quan trong tour Th&aacute;m Hiểm T&uacute; L&agrave;n trước đ&acirc;y. Hang Ti&ecirc;n nay đ&atilde; ch&iacute;nh thức được đi v&agrave;o khai th&aacute;c v&agrave; được mong đợi sẽ trở th&agrave;nh một điểm nhấn độc đ&aacute;o của tour du lịch th&aacute;m hiểm một ng&agrave;y của c&ocirc;ng ty. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!&nbsp;</p>\r\n', '<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Chuyến Kh&aacute;m Ph&aacute;&nbsp;<strong>Hang Ti&ecirc;n</strong>&nbsp;1 Ng&agrave;y được đ&aacute;nh gi&aacute; l&agrave; dễ v&agrave; ph&ugrave; hợp cho những người c&oacute; kiến thức trekking cơ bản. Tuy nhi&ecirc;n, h&atilde;y đảm bảo thể lực của qu&yacute; kh&aacute;ch ph&ugrave; hợp với c&aacute;c hoạt động sau cho chuyến đi:</p>\r\n\r\n<p>- Vượt 8 km rừng n&uacute;i với địa h&igrave;nh đ&aacute;.</p>\r\n\r\n<p>- Đi 4 km kh&aacute;m ph&aacute; hang.&nbsp;</p>\r\n\r\n<p>- Với những h&igrave;nh th&agrave;nh từ tr&ecirc;n xuống, hệ thống thạch nhũ tầng tầng lớp lớp của hang Ti&ecirc;n được v&iacute; như ruộng bậc thang, kết hợp với &aacute;nh s&aacute;ng ngo&agrave;i trời chiếu rọi v&agrave;o trong tạo n&ecirc;n sự đa dạng cho thực vật, hang Ti&ecirc;n thực sự l&agrave; một bức tranh thi&ecirc;n nhi&ecirc;n độc đ&aacute;o v&agrave; h&ugrave;ng vĩ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<p><strong>LỊCH TR&Igrave;NH: (Ăn trưa)</strong></p>\r\n\r\n<p><strong>Điểm đ&oacute;n:&nbsp;</strong>Kh&aacute;ch đi tour sẽ tại kh&aacute;ch sạn ở&nbsp;<strong>Phong Nha</strong>&nbsp;v&agrave; gi&aacute; c&aacute;c tour đ&atilde; bao gồm đ&oacute;n/trả kh&aacute;ch ở Phong Nha. Nếu trong trường hợp&nbsp;kh&aacute;ch ở tại&nbsp;<strong>Đồng Hới</strong>&nbsp;kh&ocirc;ng ở&nbsp;Kh&aacute;ch sạn tại&nbsp;<strong>Phong Nha</strong>&nbsp;th&igrave; sẽ tự di chuyển&nbsp;l&ecirc;n Phong Nha để tham gia tour với đo&agrave;n.&nbsp;Từ Đồng Hới - Phong Nha khoảng 55km, mất khoảng 1 tiếng di chuyển. V&igrave; thế kh&aacute;ch từ<strong>&nbsp;Đồng Hới</strong>&nbsp;di chuyển sang&nbsp;<strong>Phong Nha</strong>&nbsp;sớm hơn để tham gia đ&uacute;ng giờ trong tour (Khuyến kh&iacute;ch kh&aacute;ch đặt ph&ograve;ng tại Phong Nha).&nbsp;</p>\r\n\r\n<p>Khởi h&agrave;nh: 07:00 - 07:30 s&aacute;ng từ c&aacute;c kh&aacute;ch sạn ở&nbsp;<strong>Phong Nha</strong>.</p>\r\n\r\n<p>Kết th&uacute;c: 07:00 - 7:30 tối tại c&aacute;c kh&aacute;ch sạn ở&nbsp;<strong>Phong Nha</strong>.</p>\r\n\r\n<p>Thời lượng: 1 ng&agrave;y (10 &ndash; 12 tiếng)</p>\r\n\r\n<p>Mức độ mạo hiểm: Vừa (8km) ph&ugrave; hợp với người năng động.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i sẽ đ&oacute;n c&aacute;c bạn tại kh&aacute;ch sạn m&agrave; bạn đ&atilde; đăng k&yacute; trước trong khu vực&nbsp;<strong>Phong Nha</strong>&nbsp;trong khoảng thời gian từ 7h đến 7h30, v&agrave; điểm đến sẽ l&agrave; văn ph&ograve;ng của c&ocirc;ng ty tại&nbsp;<strong>T&acirc;n H&oacute;a</strong>, c&aacute;ch&nbsp;<strong>Phong Nha</strong>&nbsp;70km.</p>\r\n\r\n<p>Tr&ecirc;n suốt qu&atilde;ng đường đi, bạn sẽ được ngắm những cảnh quan h&ugrave;ng vĩ của n&uacute;i rừng, những c&aacute;nh đồng l&uacute;a bạt ng&agrave;n v&agrave; đi qua di t&iacute;ch&nbsp;<strong>Đ&egrave;o Đ&aacute; Đẽo</strong>.</p>\r\n\r\n<p>Bạn sẽ đến văn ph&ograve;ng c&ocirc;ng ty du lịch tại&nbsp;<strong>T&acirc;n H&oacute;a</strong>&nbsp;v&agrave;o khoảng 9h00 để tham gia buổi phổ biến an to&agrave;n. Sau khi t&igrave;m hiểu tất cả c&aacute;c th&ocirc;ng tin về t&iacute;nh an to&agrave;n của chuyến đi, ho&agrave;n th&agrave;nh c&ocirc;ng t&aacute;c thu xếp h&agrave;nh l&yacute; v&agrave; k&yacute;&nbsp;<strong>x&aacute;c nhận v&agrave;o Phiếu miễn trừ tr&aacute;ch nhiệm</strong>. Bạn c&oacute; thể để lại to&agrave;n bộ c&aacute;c h&agrave;nh l&iacute; kh&ocirc;ng cần thiết tại tủ giữ đồ của văn ph&ograve;ng.</p>\r\n\r\n<p>V&agrave;o khoảng 9h30 s&aacute;ng, đo&agrave;n sẽ được chở đến điểm xuất ph&aacute;t v&agrave; bắt đầu h&agrave;nh tr&igrave;nh v&agrave;o khoảng 10 giờ s&aacute;ng. Chuyến đi sẽ bắt đầu bằng việc leo qua một ngọn đồi để v&agrave;o rừng. V&agrave;o những đoạn đường cuối c&ugrave;ng để v&agrave;o hang sẽ c&oacute; rất nhiều địa h&igrave;nh đ&aacute; gập ghềnh, tạo cảm gi&aacute;c mạo hiểm v&agrave; kỳ th&uacute; dẫn dắt bạn đến với cảnh quan tuyệt vời đến cửa hang.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/09/23/11/ivivu-trekk-hang-tien.gif\" /></p>\r\n\r\n<p><em>Trekking Hang Ti&ecirc;n.</em></p>\r\n\r\n<p>Giữa cảnh quan h&ugrave;ng vĩ v&agrave; xinh đẹp của n&uacute;i rừng, bạn sẽ trải nghiệm một bữa trưa hấp dẫn với m&oacute;n thịt nướng cuốn b&aacute;nh ướt (thực đơn ăn chay sẽ được phục vụ theo y&ecirc;u cầu). V&agrave;o khoảng 12h30 ph&uacute;t trưa, đo&agrave;n sẽ bắt đầu kh&aacute;m ph&aacute; hang động.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/09/23/11/ivivu-ben-ngoai-hang-tien.gif\" /></p>\r\n\r\n<p><em>Bắt Đầu Kh&aacute;m Ph&aacute; Hang.</em></p>\r\n\r\n<p>Hệ thống thạch nhũ tầng tầng lớp lớp của&nbsp;<strong>hang Ti&ecirc;n</strong>&nbsp;được v&iacute; như ruộng bậc thang, kết hợp với &aacute;nh s&aacute;ng ngo&agrave;i trời chiếu rọi v&agrave;o trong tạo n&ecirc;n sự đa dạng cho thực vật, hang Ti&ecirc;n thực sự l&agrave; một bức tranh thi&ecirc;n nhi&ecirc;n độc đ&aacute;o v&agrave; h&ugrave;ng vĩ.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/09/23/11/ivivu-ben-trong-hang-tien.gif\" /></p>\r\n\r\n<p><em>Kh&aacute;m Ph&aacute; B&ecirc;n Trong Hang Ti&ecirc;n.</em></p>\r\n\r\n<p>Những &aacute;nh s&aacute;ng hắt v&agrave;o từ cửa hang, sẽ tạo n&ecirc;n một&nbsp; bầu trời tr&ecirc;n m&aacute;i v&ograve;m. Ngay b&ecirc;n cạnh bầu trời lấp l&aacute;nh l&agrave; một lối đi nhỏ dẫn l&ecirc;n phần thứ hai của hang, nơi được mi&ecirc;u tả như một &ldquo;căn ph&ograve;ng tr&ecirc;n cao&rdquo;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/09/23/11/ivivu-bac-thang-hang-tien.gif\" /></p>\r\n\r\n<p><em>Hang Ti&ecirc;n.</em></p>\r\n\r\n<p>Lối ra cửa hang sẽ nằm ngay trước tầm mắt của bạn, bạn đ&atilde; chinh phục được&nbsp;<strong>Hang Ti&ecirc;n</strong>!</p>\r\n\r\n<p>Tr&ecirc;n đường trở về, bạn sẽ c&oacute; cơ hội đắm m&igrave;nh v&agrave;o hồ bơi tự nhi&ecirc;n khổng lồ ngay giữa n&uacute;i rừng, bơi lội, chụp h&igrave;nh, thưởng thức một t&aacute;ch tr&agrave; hay c&agrave; ph&ecirc; n&oacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/09/23/11/ivivu-hang-tien.gif\" /></p>\r\n\r\n<p>&nbsp;<em>Hồ Bơi Tự Nhi&ecirc;n.</em></p>\r\n', 1, 5),
(31, 'du lich hội an', '2222', '1230000', 22, 'pt.jpeg', '<p>ưqe</p>\r\n', '<p>ưqeqwe</p>\r\n', 0, 6);

